    <script>
        function updateCipher() {
            const shiftValue = parseInt(document.getElementById('shift-slider').value);
            const textInput = document.getElementById('text-input').value;
            const plaintextAlphabet = document.getElementById('plaintext-alphabet');
            const ciphertextAlphabet = document.getElementById('ciphertext-alphabet');
            const encryptionKeyDisplay = document.getElementById('encryption-key-display');
            const decryptionKeyDisplay = document.getElementById('decryption-key-display');
            const plaintextOutput = document.getElementById('plaintext-output');
            const ciphertextOutput = document.getElementById('ciphertext-output');

            // Update alphabet mapping
            ciphertextAlphabet.innerHTML = '';
            for (let i = 0; i < 26; i++) {
                const charCode = 'A'.charCodeAt(0) + (i + shiftValue) % 26;
                const char = String.fromCharCode(charCode);
                const span = document.createElement('span');
                span.textContent = char;
                ciphertextAlphabet.appendChild(span);
            }

            // Update key displays
            encryptionKeyDisplay.textContent = `encryption key: ${shiftValue}`;
            const decryptionKey = (26 - shiftValue) % 26;
            decryptionKeyDisplay.textContent = `decryption key: ${decryptionKey}`;

            // Update text output
            plaintextOutput.textContent = `plaintext : ${textInput}`;
            ciphertextOutput.textContent = `ciphertext: ${encrypt(textInput, shiftValue)}`;
        }

        function encrypt(text, shift) {
            let ciphertext = '';
            for (let i = 0; i < text.length; i++) {
                const char = text[i];
                if (char.match(/[a-z]/)) {
                    const charCode = char.charCodeAt(0);
                    const shiftedCharCode = (charCode - 'a'.charCodeAt(0) + shift) % 26 + 'a'.charCodeAt(0);
                    ciphertext += String.fromCharCode(shiftedCharCode);
                } else {
                    ciphertext += char;
                }
            }
            return ciphertext;
        }

        document.getElementById('shift-slider').addEventListener('input', updateCipher);
        document.getElementById('text-input').addEventListener('input', updateCipher);

        // Initial update
        updateCipher();
    </script>
</body>
</html>
