        }

        #control-panel input[type="range"] {
            width: 150px;
        }

        #main-display {
            position: absolute;
            top: 50px;
            left: 10px;
            border: 1px solid #ccc;
            padding: 10px;
        }

        #canvas-container {
            width: 600px;
            height: 600px;
            border: 1px solid #ccc;
        }

        #ratio-text {
            font-size: 18px;
            font-weight: bold;
        }

        .active {
            background-color: #ccc;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="coordination-buttons">
                <label>coordination:</label>
                <button id="btn-threefold" class="active">threefold</button>
                <button id="btn-fourfold">fourfold</button>
                <button id="btn-sixfold">sixfold</button>
                <button id="btn-eightfold">eightfold</button>
            </div>
            <div id="anion-radius-slider">
                <label>relative anion radius:</label>
                <input type="range" id="slider-anion-radius" min="0.1" max="1.0" step="0.001" value="0.1">
                <span id="value-anion-radius">0.100</span>
            </div>
            <div id="cation-radius-slider">
                <label>relative cation radius:</label>
                <input type="range" id="slider-cation-radius" min="0.1" max="1.0" step="0.001" value="0.1">
                <span id="value-cation-radius">0.100</span>
            </div>
            <div id="opacity-slider">
                <label>opacity of anions:</label>
                <input type="range" id="slider-opacity" min="0.0" max="1.0" step="0.01" value="0.8">
            </div>
        </div>
        <div id="main-display">
            <div id="canvas-container"></div>
            <p id="ratio-text">The ratio of the cation radius to the anion radius is 1.00.</p>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        // Initialize the scene, camera, and renderer
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({
            canvas: document.getElementById('canvas-container'),
            antialias: true
        });
        renderer.setSize(window.innerWidth, window.innerHeight);
        camera.position.z = 5;

        // Initialize the controls
        const controls = new THREE.OrbitControls(camera, renderer.domElement);

        // Initialize the lights
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
        scene.add(ambientLight);
        const pointLight = new THREE.PointLight(0xffffff, 0.8);
        pointLight.position.set(5, 5, 5);
        scene.add(pointLight);

        // Initialize the cation
        const cationGeometry = new THREE.SphereGeometry(0.1, 32, 32);
        const cationMaterial = new THREE.MeshPhongMaterial({
            color: 0x0000ff,
            specular: 0xffffff,
            shininess: 100
        });
        const cationMesh = new THREE.Mesh(cationGeometry, cationMaterial);
        cationMesh.position.set(0, 0, 0);
        scene.add(cationMesh);

        // Initialize the anions
        const anionGeometry = new THREE.SphereGeometry(0.1, 32, 32);
        const anionMaterial = new THREE.MeshPhongMaterial({
            color: 0xe0d8e0,
            specular: 0xffffff,
            shininess: 100,
            transparent: true
        });
        const anionMeshes = [];
        for (let i = 0; i < 8; i++) {
            const anionMesh = new THREE.Mesh(anionGeometry, anionMaterial);
            anionMesh.position.set(Math.sin(i * Math.PI / 4) * 1.5, Math.cos(i * Math.PI / 4) * 1.5, 0);
            scene.add(anionMesh);
            anionMeshes.push(anionMesh);
        }

        // Initialize the bounding box
        const boundingBoxGeometry = new THREE.BoxGeometry(2, 2, 2);
        const boundingBoxMaterial = new THREE.LineBasicMaterial({ color: 0x888888 });
        const boundingBoxMesh = new THREE.LineSegments(boundingBoxGeometry, boundingBoxMaterial);
        boundingBoxMesh.position.set(0, 0, 0);
        scene.add(boundingBoxMesh);

        // Initialize the ratio text
        const ratioText = document.getElementById('ratio-text');

        // Function to update the ratio text
        function updateRatioText() {
            const cationRadius = parseFloat(document.getElementById('slider-cation-radius').value);
            const anionRadius = parseFloat(document.getElementById('slider-anion-radius').value);
            const ratio = cationRadius / anionRadius;
            ratioText.textContent = `The ratio of the cation radius to the anion radius is ${ratio.toFixed(2)}.`;
        }

        // Function to update the anion radius
        function updateAnionRadius() {
            const anionRadius = parseFloat(document.getElementById('slider-anion-radius').value);
            anionMaterial.opacity = 1 - parseFloat(document.getElementById('slider-opacity').value);
            for (const anionMesh of anionMeshes) {
                anionMesh.scale.set(anionRadius, anionRadius, anionRadius);
            }
            updateRatioText();
        }

        // Function to update the cation radius
        function updateCationRadius() {
            const cationRadius = parseFloat(document.getElementById('slider-cation-radius').value);
            cationMesh.scale.set(cationRadius, cationRadius, cationRadius);
            updateRatioText();
        }

        // Function to update the opacity
        function updateOpacity() {
            const anionOpacity = 1 - parseFloat(document.getElementById('slider-opacity').value);
            anionMaterial.opacity = anionOpacity;
            updateRatioText();
        }

        // Function to handle button clicks
        function handleButtonClick(event) {
            const button = event.target;
            const activeButton = document.querySelector('.active');
            if (activeButton) {
                activeButton.classList.remove('active');
            }
            button.classList.add('active');
            const coordination = button.textContent.toLowerCase();
            scene.clear();
            switch (coordination) {
                case 'threefold':
                    for (const anionMesh of anionMeshes) {
                        anionMesh.position.set(Math.sin(i * Math.PI / 3) * 1.5, Math.cos(i * Math.PI / 3) * 1.5, 0);
                    }
                    break;
                case 'fourfold':
                    for (const anionMesh of anionMeshes) {
                        anionMesh.position.set(Math.sin(i * Math.PI / 4) * 1.5, Math.cos(i * Math.PI / 4) * 1.5, 0);
                    }
                    boundingBoxMesh.position.set(0, 0, 0);
                    break;
                case 'sixfold':
                    for (const anionMesh of anionMeshes) {
                        anionMesh.position.set(Math.sin(i * Math.PI / 6) * 1.5, Math.cos(i * Math.PI / 6) * 1.5, 0);
                    }
                    boundingBoxMesh.position.set(0, 0, 0);
                    break;
                case 'eightfold':
                    for (const anionMesh of anionMeshes) {
                        anionMesh.position.set(Math.sin(i * Math.PI / 8) * 1.5, Math.cos(i * Math.PI / 8) * 1.5, 0);
                    }
                    boundingBoxMesh.position.set(0, 0, 0);
                    break;
            }
            camera.position.z = 5;
            controls.reset();
        }

        // Add event listeners
        document.getElementById('btn-threefold').addEventListener('click', handleButtonClick);
        document.getElementById('btn-fourfold').addEventListener('click', handleButtonClick);
        document.getElementById('btn-sixfold').addEventListener('click', handleButtonClick);
        document.getElementById('btn-eightfold').addEventListener('click', handleButtonClick);
        document.getElementById('slider-anion-radius').addEventListener('input', updateAnionRadius);
        document.getElementById('slider-cation-radius').addEventListener('input', updateCationRadius);
        document.getElementById('slider-opacity').addEventListener('input', updateOpacity);

        // Render the scene
        function animate() {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        }
        animate();

        // Update the ratio text
        updateRatioText();
    </script>
</body>
</html>
