<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Inclined Plane Simulation</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            display: flex;
            gap: 10px;
        }
        #control-panel {
            flex-basis: 200px;
            padding: 20px;
            border: 1px solid #ccc;
            border-radius: 10px;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }
        #control-panel input[type="range"] {
            transform: rotate(270deg);
            writing-mode: bt-lr;
        }
        #visualization-area {
            flex-grow: 1;
            padding: 20px;
        }
        #p5-canvas {
            border: 1px solid #ccc;
        }
        #info-display {
            font-size: 18px;
            font-weight: bold;
            margin-bottom: 20px;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <input type="range" id="slider-angle" min="10" max="80" value="30" step="1">
        <label for="slider-angle">slope θ</label>
        <div id="animation-controls">
            <p>release block</p>
            <button id="btn-play">▶</button>
            <button id="btn-pause">||</button>
            <button id="btn-reset">|<</button>
        </div>
    </div>
    <div id="visualization-area">
        <p id="info-display"></p>
        <canvas id="p5-canvas" width="800" height="600"></canvas>
    </div>
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/dist/p5.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3.2.0/es5/tex-mml-chtml.js"></script>
    <script>
        let theta = 30;
        let isPlaying = false;
        let angleSlider;
        let playButton;
        let pauseButton;
        let resetButton;
        let canvas;
        let ctx;
        let blockX = 50;
        let blockY = 50;
        let gravityVectorX = 0;
        let gravityVectorY = -9.81 * Math.sin(theta * Math.PI / 180);
        let normalVectorX = 0;
        let normalVectorY = 9.81 * Math.cos(theta * Math.PI / 180);
        let netForceVectorX = -9.81 * Math.sin(theta * Math.PI / 180);
        let netForceVectorY = 0;
        let blockVelocityX = 0;
        let blockVelocityY = 0;
        let blockAccelerationX = 0;
        let blockAccelerationY = 0;
        let startTime = 0;

        function setup() {
            canvas = createCanvas(800, 600);
            ctx = canvas.getContext('2d');
            angleSlider = document.getElementById('slider-angle');
            playButton = document.getElementById('btn-play');
            pauseButton = document.getElementById('btn-pause');
            resetButton = document.getElementById('btn-reset');
            angleSlider.addEventListener('input', updateAngle);
            playButton.addEventListener('click', startAnimation);
            pauseButton.addEventListener('click', pauseAnimation);
            resetButton.addEventListener('click', resetAnimation);
            updateInfoDisplay();
        }

        function draw() {
            if (isPlaying) {
                updateBlockPosition();
                updateVectors();
                updateInfoDisplay();
            }
            drawInclinedPlane();
            drawBlock();
            drawGravityVector();
            drawNormalVector();
            drawNetForceVector();
            drawVectorDecomposition();
            drawCoordinateAxes();
            drawAngleMarker();
        }

        function updateAngle() {
            theta = angleSlider.value;
            isPlaying = false;
            resetAnimation();
            updateInfoDisplay();
        }

        function startAnimation() {
            isPlaying = true;
            startTime = millis();
        }

        function pauseAnimation() {
            isPlaying = false;
        }

        function resetAnimation() {
            isPlaying = false;
            blockX = 50;
            blockY = 50;
            blockVelocityX = 0;
            blockVelocityY = 0;
            blockAccelerationX = 0;
            blockAccelerationY = 0;
            startTime = 0;
            updateVectors();
            updateInfoDisplay();
        }

        function updateBlockPosition() {
            let time = millis() - startTime;
            let acceleration = 9.81 * Math.sin(theta * Math.PI / 180);
            blockVelocityX = blockAccelerationX * time;
            blockVelocityY = blockAccelerationY * time + 0.5 * acceleration * time * time;
            blockX = 50 + blockVelocityX;
            blockY = 50 + blockVelocityY;
        }

        function updateVectors() {
            gravityVectorX = 0;
            gravityVectorY = -9.81 * Math.sin(theta * Math.PI / 180);
            normalVectorX = 0;
            normalVectorY = 9.81 * Math.cos(theta * Math.PI / 180);
            netForceVectorX = -9.81 * Math.sin(theta * Math.PI / 180);
            netForceVectorY = 0;
        }

        function drawInclinedPlane() {
            ctx.save();
            ctx.translate(400, 300);
            ctx.rotate(theta * Math.PI / 180);
            ctx.beginPath();
            ctx.moveTo(-200, 0);
            ctx.lineTo(200, 0);
            ctx.lineTo(0, -200);
            ctx.fillStyle = 'lightblue';
            ctx.fill();
            ctx.strokeStyle = 'yellow';
            ctx.lineWidth = 2;
            ctx.stroke();
            ctx.restore();
        }

        function drawBlock() {
            ctx.fillStyle = 'darkgrey';
            ctx.fillRect(blockX - 10, blockY - 10, 20, 20);
        }

        function drawGravityVector() {
            ctx.save();
            ctx.translate(blockX + 10, blockY + 10);
            ctx.rotate(Math.atan2(gravityVectorY, gravityVectorX));
            ctx.beginPath();
            ctx.moveTo(0, 0);
            ctx.lineTo(0, -50);
            ctx.strokeStyle = 'red';
            ctx.lineWidth = 2;
            ctx.stroke();
            ctx.restore();
            ctx.font = '12px Arial';
            ctx.textAlign = 'left';
            ctx.textBaseline = 'top';
            ctx.fillStyle = 'red';
            ctx.fillText('F_grav = mg', blockX + 10, blockY + 10);
        }

        function drawNormalVector() {
            ctx.save();
            ctx.translate(blockX + 10, blockY + 10);
            ctx.rotate(Math.atan2(normalVectorY, normalVectorX));
            ctx.beginPath();
            ctx.moveTo(0, 0);
            ctx.lineTo(0, -50);
            ctx.strokeStyle = 'deepskyblue';
            ctx.lineWidth = 2;
            ctx.stroke();
            ctx.restore();
            ctx.font = '12px Arial';
            ctx.textAlign = 'left';
            ctx.textBaseline = 'top';
            ctx.fillStyle = 'deepskyblue';
            ctx.fillText('"normal" force', blockX + 10, blockY + 10);
        }

        function drawNetForceVector() {
            ctx.save();
            ctx.translate(blockX + 10, blockY + 10);
            ctx.rotate(Math.atan2(netForceVectorY, netForceVectorX));
            ctx.beginPath();
            ctx.moveTo(0, 0);
            ctx.lineTo(0, -50);
            ctx.strokeStyle = 'purple';
            ctx.lineWidth = 2;
            ctx.stroke();
            ctx.restore();
            ctx.font = '12px Arial';
            ctx.textAlign = 'left';
            ctx.textBaseline = 'top';
            ctx.fillStyle = 'purple';
            ctx.fillText('F_net = m g sin(θ)', blockX + 10, blockY + 10);
        }

        function drawVectorDecomposition() {
            ctx.save();
            ctx.translate(blockX + 10, blockY + 10);
            ctx.rotate(Math.atan2(gravityVectorY, gravityVectorX));
            ctx.beginPath();
            ctx.moveTo(0, 0);
            ctx.lineTo(-25, 0);
            ctx.strokeStyle = 'black';
            ctx.lineWidth = 1;
            ctx.stroke();
            ctx.font = '12px Arial';
            ctx.textAlign = 'left';
            ctx.textBaseline = 'top';
            ctx.fillStyle = 'black';
            ctx.fillText('m g cos(θ)', blockX + 10, blockY + 10);
            ctx.restore();
            ctx.save();
            ctx.translate(blockX + 10, blockY + 10);
            ctx.rotate(Math.atan2(netForceVectorY, netForceVectorX));
            ctx.beginPath();
            ctx.moveTo(0, 0);
            ctx.lineTo(25, 0);
            ctx.strokeStyle = 'black';
            ctx.lineWidth = 1;
            ctx.stroke();
            ctx.restore();
        }

        function drawCoordinateAxes() {
            ctx.save();
            ctx.translate(400, 300);
            ctx.rotate(theta * Math.PI / 180);
            ctx.beginPath();
            ctx.moveTo(-200, 0);
            ctx.lineTo(200, 0);
            ctx.strokeStyle = 'black';
            ctx.lineWidth = 1;
            ctx.stroke();
            ctx.font = '12px Arial';
            ctx.textAlign = 'left';
            ctx.textBaseline = 'top';
            ctx.fillStyle = 'black';
            ctx.fillText('x', 220, 0);
            ctx.restore();
            ctx.save();
            ctx.translate(400, 300);
            ctx.rotate(theta * Math.PI / 180);
            ctx.beginPath();
            ctx.moveTo(0, -200);
            ctx.lineTo(0, 200);
            ctx.strokeStyle = 'black';
            ctx.lineWidth = 1;
            ctx.stroke();
            ctx.font = '12px Arial';
            ctx.textAlign = 'left';
            ctx.textBaseline = 'top';
            ctx.fillStyle = 'black';
            ctx.fillText('y', 0, 220);
            ctx.restore();
        }

        function drawAngleMarker() {
            ctx.save();
            ctx.translate(400, 300);
            ctx.rotate(theta * Math.PI / 180);
            ctx.beginPath();
            ctx.arc(0, 0, 20, 0, Math.PI);
            ctx.fillStyle = 'black';
            ctx.fill();
            ctx.font = '12px Arial';
            ctx.textAlign = 'center';
            ctx.textBaseline = 'middle';
            ctx.fillStyle = 'black';
            ctx.fillText('θ', 0, 0);
            ctx.restore();
        }

        function updateInfoDisplay() {
            let acceleration = 9.81 * Math.sin(theta * Math.PI / 180);
            document.getElementById('info-display').innerHTML = `slope = \\( \\theta \\) = ${theta}°; acceleration = \\( g \\sin(\\theta) \\) = ${acceleration.toFixed(3)} m/s²`;
            MathJax.typeset();
        }

        setup();
        draw();
    </script>
</body>
</html>
