            white-space: pre;
            font-family: monospace;
        }
    </style>
</head>
<body>
    <div id="main">
        <div id="title-and-description">
            <h1>Algorithm for Data Encryption Standard</h1>
            <p>This demo implements the Data Encryption Standard (DES) algorithm for encryption. The user can input a message and a key, and select the number of encryption steps. The output will display the encrypted message for each even-numbered step.</p>
        </div>
        <div id="control-panel">
            <div class="input-group">
                <label for="input-message">message</label>
                <input type="text" id="input-message" value="hello">
            </div>
            <div class="input-group">
                <label for="input-key">key</label>
                <input type="text" id="input-key" value="test">
            </div>
            <div class="radio-group">
                <input type="radio" id="radio-steps-2" name="steps" value="2">
                <label for="radio-steps-2">2</label>
                <input type="radio" id="radio-steps-4" name="steps" value="4">
                <label for="radio-steps-4">4</label>
                <input type="radio" id="radio-steps-8" name="steps" value="8">
                <label for="radio-steps-8">8</label>
                <input type="radio" id="radio-steps-16" name="steps" value="16" checked>
                <label for="radio-steps-16">16</label>
            </div>
        </div>
        <div id="output-container">
            <div id="output-header">
                <span id="output-header-permutations">Permutations in Encryption</span>
                <span id="output-header-message">Encrypted Message</span>
            </div>
            <pre id="output-results"></pre>
        </div>
    </div>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <script>
        const inputMessage = document.getElementById('input-message');
        const inputKey = document.getElementById('input-key');
        const radioSteps = document.querySelectorAll('input[name="steps"]');
        const outputResults = document.getElementById('output-results');

        let message = inputMessage.value;
        let key = inputKey.value;
        let steps = parseInt(radioSteps[3].value);

        function updateDisplay() {
            message = inputMessage.value;
            key = inputKey.value;
            steps = parseInt(radioSteps.find(r => r.checked).value);

            let binaryMessage = message.split('').map(c => c.charCodeAt(0).toString(2).padStart(8, '0')).join('');
            let paddedMessage = binaryMessage.padEnd(64 * Math.ceil(binaryMessage.length / 64), '0');
            let messageBlocks = [];
            for (let i = 0; i < paddedMessage.length; i += 64) {
                messageBlocks.push(paddedMessage.slice(i, i + 64));
            }

            let binaryKey = key.split('').map(c => c.charCodeAt(0).toString(2).padStart(8, '0')).join('');
            let paddedKey = binaryKey.padEnd(64, '0');
            let keyBlocks = [];
            for (let i = 0; i < paddedKey.length; i += 64) {
                keyBlocks.push(paddedKey.slice(i, i + 64));
            }

            let ciphertext = '';
            for (let s = 2; s <= steps; s += 2) {
                let currentStepCiphertext = '';
                for (let i = 0; i < messageBlocks.length; i++) {
                    let block = messageBlocks[i];
                    let encryptedBlock = desEncryption(block, keyBlocks, s);
                    currentStepCiphertext += encryptedBlock.toString(16).padStart(16, '0');
                }
                ciphertext += `${s.toString().padStart(2, ' ') + ' ' + currentStepCiphertext}\n`;
            }

            outputResults.textContent = ciphertext;
        }

        function desEncryption(block, keyBlocks, s) {
            let expandedBlock = expandBlock(block);
            let permutationBlock = permutation(expandedBlock);
            let keySchedule = generateKeySchedule(keyBlocks, s);
            let ciphertext = '';
            for (let i = 0; i < s; i++) {
                let roundKey = keySchedule[i];
                let roundBlock = permutationBlock.slice(0, 32);
                roundBlock = roundBlock.split('').map((c, j) => {
                    if (j < 28) {
                        return c;
                    } else {
                        return (parseInt(c) + parseInt(roundKey[j - 28])) % 256;
                    }
                }).join('');
                roundBlock = permutation(roundBlock);
                ciphertext += roundBlock.slice(0, 32);
                permutationBlock = roundBlock.slice(32);
            }
            return ciphertext.slice(0, 32);
        }

        function expandBlock(block) {
            let expandedBlock = '';
            for (let i = 0; i < 32; i++) {
                expandedBlock += block.slice(i * 6, i * 6 + 6);
            }
            return expandedBlock;
        }

        function permutation(block) {
            let permutationTable = [58, 50, 42, 34, 26, 18, 10, 2, 60, 52, 44, 36, 28, 20, 12, 4, 62, 54, 46, 38, 30, 22, 14, 6, 64, 56, 48, 40, 32, 24, 16, 8];
            let permutedBlock = '';
            for (let i = 0; i < 64; i++) {
                permutedBlock += block[permutationTable[i] - 1];
            }
            return permutedBlock;
        }

        function generateKeySchedule(keyBlocks, s) {
            let keySchedule = [];
            for (let i = 0; i < s; i++) {
                let roundKey = keyBlocks[i % keyBlocks.length];
                let rotatedRoundKey = '';
                for (let j = 0; j < 28; j++) {
                    rotatedRoundKey += roundKey[(j + i) % 28];
                }
                keySchedule.push(rotatedRoundKey);
            }
            return keySchedule;
        }

        updateDisplay();
        inputMessage.addEventListener('input', updateDisplay);
        inputKey.addEventListener('input', updateDisplay);
        radioSteps.forEach(r => r.addEventListener('change', updateDisplay));
    </script>
</body>
</html>
