<body>
    <div id="demo-container">
        <!-- Control Panel Section -->
        <div id="control-panel">
            <button id="btn-reset">+</button>
            <div class="slider-row">
                <label for="slider-x1">x₁</label>
                <input type="range" id="slider-x1" min="-1000" max="1000" step="1" value="500">
                <span id="value-x1"></span>
            </div>
            <div class="slider-row">
                <label for="slider-v1">v₁</label>
                <input type="range" id="slider-v1" min="-15" max="15" step="0.01" value="-10">
                <span id="value-v1"></span>
            </div>
            <div class="slider-row">
                <label for="slider-x2">x₂</label>
                <input type="range" id="slider-x2" min="-1000" max="1000" step="1" value="100">
                <span id="value-x2"></span>
            </div>
            <div class="slider-row">
                <label for="slider-v2">v₂</label>
                <input type="range" id="slider-v2" min="-15" max="15" step="0.01" value="5">
                <span id="value-v2"></span>
            </div>
        </div>

        <!-- Visualization Section -->
        <div id="visualization-area">
            <h2 id="result-text"></h2>
            <div id="plot-container"></div>
        </div>

    </div>

    <!-- CDN for Plotly.js -->
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        // Get elements
        const sliderX1 = document.getElementById('slider-x1');
        const sliderV1 = document.getElementById('slider-v1');
        const sliderX2 = document.getElementById('slider-x2');
        const sliderV2 = document.getElementById('slider-v2');
        const valueX1 = document.getElementById('value-x1');
        const valueV1 = document.getElementById('value-v1');
        const valueX2 = document.getElementById('value-x2');
        const valueV2 = document.getElementById('value-v2');
        const resultText = document.getElementById('result-text');
        const plotContainer = document.getElementById('plot-container');

        // Initialize plot
        const plotData = [
            {
                x: [0, 1000],
                y: [parseInt(sliderX1.value), parseInt(sliderX1.value) + 1000 * parseFloat(sliderV1.value)],
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: 'rgb(70, 130, 180)',
                    width: 2
                }
            },
            {
                x: [0, 1000],
                y: [parseInt(sliderX2.value), parseInt(sliderX2.value) + 1000 * parseFloat(sliderV2.value)],
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: 'rgb(255, 165, 0)',
                    width: 2
                }
            }
        ];
        const layout = {
            title: 't',
            xaxis: {
                title: 't',
                range: [0, 1000],
                gridwidth: 1,
                gridcolor: 'lightgray',
                zerolinecolor: 'darkgray'
            },
            yaxis: {
                title: 'distance',
                autorange: true,
                gridwidth: 1,
                gridcolor: 'lightgray',
                zerolinecolor: 'darkgray'
            },
            margin: {
                t: 20,
                r: 20,
                b: 40,
                l: 60
            },
            showlegend: false
        };
        Plotly.newPlot(plotContainer, plotData, layout);

        // Update plot and result text on slider change
        sliderX1.addEventListener('input', updatePlot);
        sliderV1.addEventListener('input', updatePlot);
        sliderX2.addEventListener('input', updatePlot);
        sliderV2.addEventListener('input', updatePlot);

        // Update plot and result text on reset button click
        const btnReset = document.getElementById('btn-reset');
        btnReset.addEventListener('click', () => {
            sliderX1.value = 500;
            sliderV1.value = -10;
            sliderX2.value = 100;
            sliderV2.value = 5;
            updatePlot();
        });

        // Update plot and result text
        function updatePlot() {
            const x1 = parseInt(sliderX1.value);
            const v1 = parseFloat(sliderV1.value);
            const x2 = parseInt(sliderX2.value);
            const v2 = parseFloat(sliderV2.value);

            // Update value displays
            valueX1.textContent = x1;
            valueV1.textContent = v1.toFixed(2);
            valueX2.textContent = x2;
            valueV2.textContent = v2.toFixed(2);

            // Calculate intersection
            if (v1 === v2) {
                resultText.textContent = 'The runners\' paths never intersect';
            } else if ((x1 - x2) / (v2 - v1) < 0 || (x1 - x2) / (v2 - v1) > 1000) {
                resultText.textContent = 'The runners\' paths never intersect';
            } else {
                const T = (x1 - x2) / (v2 - v1);
                const X = x1 + v1 * T;
                resultText.textContent = `T = ${(T).toFixed(2)} s | X = ${(X).toFixed(2)} m`;
            }

            // Update plot
            plotData[0].y = [x1, x1 + 1000 * v1];
            plotData[1].y = [x2, x2 + 1000 * v2];
            if ((x1 - x2) / (v2 - v1) < 0 || (x1 - x2) / (v2 - v1) > 1000) {
                plotData[2] = {
                    x: [],
                    y: [],
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: 'lightgreen',
                        symbol: 'circle',
                        size: 10
                    }
                };
            } else {
                plotData[2] = {
                    x: [(x1 - x2) / (v2 - v1)],
                    y: [x1 + v1 * ((x1 - x2) / (v2 - v1))],
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: 'lightgreen',
                        symbol: 'circle',
                        size: 10
                    }
                };
            }
            Plotly.react(plotContainer, plotData, layout);
        }
    </script>
</body>
</html>
