            ellipse(50, height - 50, 200, 200);
            ellipse(150, height - 50, 200, 200);
            fill(0); // Dark blue
            text('Water', 70, height - 20);

            // Draw dynamic elements based on slider value
            if (sliderValue >= 1) {
                stroke(0); // Black
                line(100, 150, 200, height / 2 + 50);
                text('Erosion', 150, height / 2 + 70);
            }
            if (sliderValue >= 2) {
                line(200, height / 2 + 50, 300, height / 2 + 150);
                text('Runoff', 250, height / 2 + 170);
            }
            if (sliderValue >= 3) {
                line(300, height / 2 + 150, 400, height / 2 + 250);
                text('Soil', 350, height / 2 + 270);
            }
            if (sliderValue >= 4) {
                line(400, height / 2 + 250, 200, height / 2 + 150);
                text('Leaching', 300, height / 2 + 170);
            }
            if (sliderValue >= 5) {
                line(200, height / 2 + 150, 350, height / 2 + 150);
                text('Plant uptake', 300, height / 2 + 170);
            }
            if (sliderValue >= 6) {
                line(350, height / 2 + 150, 250, height / 2 + 100);
                text('Animals eat plants', 300, height / 2 + 120);
            }
            if (sliderValue >= 7) {
                line(250, height / 2 + 100, 200, height / 2 + 150);
                text('Decomposition', 250, height / 2 + 170);
            }
            if (sliderValue >= 8) {
                line(200, height / 2 + 150, 50, height - 50);
                text('Geologic uplift', 150, height - 20);
            }

            // Check for hover on labels
            for (let i = 0; i < labels.length; i++) {
                let x = labels[i][0];
                let y = labels[i][1];
                let w = labels[i][2];
                let h = labels[i][3];
                if (mouseX > x && mouseX < x + w && mouseY > y && mouseY < y + h) {
                    tooltipVisible = true;
                    tooltipText = labels[i][4];
                    tooltipX = mouseX + 10;
                    tooltipY = mouseY + 10;
                } else {
                    tooltipVisible = false;
                }
            }

            // Draw tooltip if visible
            if (tooltipVisible) {
                fill(255); // White
                noStroke();
                rect(tooltipX, tooltipY, 150, 20);
                fill(0); // Black
                textSize(12);
                text(tooltipText, tooltipX + 10, tooltipY + 15);
            }
        }

        // Define labels for hover detection
        let labels = [
            [50, 70, 100, 100, 'Clouds'],
            [220, height / 2 + 70, 100, 100, 'Rocks'],
            [220, height / 2 + 180, 100, 20, 'Animals'],
            [320, height / 2 + 140, 100, 20, 'Plants'],
            [70, height - 20, 100, 20, 'Water'],
            [150, height / 2 + 70, 100, 20, 'Erosion'],
            [250, height / 2 + 170, 100, 20, 'Runoff'],
            [350, height / 2 + 270, 100, 20, 'Soil'],
            [300, height / 2 + 170, 100, 20, 'Leaching'],
            [300, height / 2 + 170, 100, 20, 'Plant uptake'],
            [300, height / 2 + 120, 100, 20, 'Animals eat plants'],
            [250, height / 2 + 170, 100, 20, 'Decomposition'],
            [150, height - 20, 100, 20, 'Geologic uplift']
        ];

        // Update slider value and redraw canvas
        sliderValue = document.getElementById('slider-phase').value;
        draw();

        // Update slider value and redraw canvas on slider change
        document.getElementById('slider-phase').addEventListener('input', function() {
            sliderValue = this.value;
            draw();
        });

        // Reset slider value and redraw canvas on reset button click
        document.getElementById('btn-reset').addEventListener('click', function() {
            sliderValue = 0;
            draw();
        });

        // Draw canvas for the first time
        draw();
    </script>
