        function drawMatchedEdges(edges, matching, isFinal) {
            for (let edgeIndex of matching) {
                let edge = edges[edgeIndex];
                stroke(isFinal ? 76, 77, 255 : 255, 77, 77);
                strokeWeight(6);
                line(edge.u.x, edge.u.y, edge.v.x, edge.v.y);
            }
