<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Interactive 3D Visualization</title>
    <style>
        body {
            margin: 0;
            background-color: #f0f0f0;
            font-family: Arial, sans-serif;
        }

        #main-container {
            display: flex;
            flex-direction: row;
            border: 1px solid #ccc;
            padding: 10px;
            width: 80%;
            margin: 40px auto;
        }

        #control-panel {
            width: 25%;
            padding: 10px;
            border-right: 1px solid #ccc;
        }

        #visualization-container {
            width: 75%;
            padding: 10px;
        }

        canvas {
            width: 100%;
            height: 600px;
            border: 1px solid #ccc;
        }

        .plus-icon {
            position: absolute;
            top: 10px;
            right: 10px;
            font-size: 24px;
            cursor: pointer;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div>
                <label for="slider-cylinder-radius">cylinder radius</label>
                <input type="range" id="slider-cylinder-radius" min="0.5" max="3.0" step="0.05" value="2.1">
                <span id="label-cylinder-radius">2.10</span>
            </div>

            <div>
                <label for="slider-opacity">opacity</label>
                <input type="range" id="slider-opacity" min="0.0" max="1.0" step="0.01" value="0.95">
                <span id="label-opacity">0.95</span>
            </div>

            <div>
                <label for="slider-drill-radius">drill radius</label>
                <input type="range" id="slider-drill-radius" min="0.1" max="2.5" step="0.05" value="1.35">
                <span id="label-drill-radius">1.35</span>
            </div>

            <div>
                <label for="slider-inclination">inclination</label>
                <input type="range" id="slider-inclination" min="0" max="1.57" step="0.01" value="0.76">
                <span id="label-inclination">0.76</span>
            </div>

            <div>
                <label for="slider-rotation">rotation around vertical</label>
                <input type="range" id="slider-rotation" min="-3.14" max="3.14" step="0.01" value="0.9">
                <span id="label-rotation">0.90</span>
            </div>

            <div>
                <label for="slider-offset">offset</label>
                <input type="range" id="slider-offset" min="-2.0" max="2.0" step="0.01" value="-0.92">
                <span id="label-offset">-0.92</span>
            </div>

            <div>
                <label for="checkbox-boundary-only">boundary curve only</label>
                <input type="checkbox" id="checkbox-boundary-only">
            </div>
        </div>

        <div id="visualization-container">
            <div class="plus-icon">+</div>
            <canvas id="main-canvas"></canvas>
        </div>
    </div>

    <script src="https://unpkg.com/three@0.158.0/build/three.module.js"></script>
    <script src="https://unpkg.com/three-bvh-csg@0.0.16/dist/index.module.js"></script>
    <script src="https://unpkg.com/three@0.158.0/examples/jsm/controls/OrbitControls.js"></script>

    <script>
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xe0e0e0);

        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.z = 5;

        const controls = new THREE.OrbitControls(camera, document.getElementById('main-canvas'));

        const renderer = new THREE.WebGLRenderer({
            canvas: document.getElementById('main-canvas'),
            antialias: true
        });
        renderer.setSize(window.innerWidth, window.innerHeight);

        const cylinderGeometry = new THREE.CylinderGeometry(1, 1, 8, 32);
        const cylinderMaterial = new THREE.MeshStandardMaterial({
            color: 0xd2b48c,
            opacity: 0.95,
            transparent: true
        });
        const cylinderMesh = new THREE.Mesh(cylinderGeometry, cylinderMaterial);
        scene.add(cylinderMesh);

        const drillCylinderGeometry = new THREE.CylinderGeometry(0.5, 0.5, 10, 32);
        const drillCylinderMaterial = new THREE.MeshStandardMaterial({
            color: 0xd2b48c,
            opacity: 0.95,
            transparent: true
        });
        const drillCylinderMesh = new THREE.Mesh(drillCylinderGeometry, drillCylinderMaterial);
        scene.add(drillCylinderMesh);

        const csg = new BVH.CSG();
        const result = csg.subtract(cylinderMesh.geometry, drillCylinderMesh.geometry);
        const resultMesh = new THREE.Mesh(result, cylinderMaterial);
        scene.add(resultMesh);

        const boundaryCurveGeometry = new THREE.BufferGeometry();
        const points = [];
        for (let t = 0; t <= 2 * Math.PI; t += 0.01) {
            const point = new THREE.Vector3(Math.cos(t), 0, Math.sin(t));
            point.applyAxisAngle(new THREE.Vector3(0, 1, 0), Math.PI / 2);
            point.applyAxisAngle(new THREE.Vector3(1, 0, 0), Math.PI / 2);
            point.applyAxisAngle(new THREE.Vector3(0, 0, 1), Math.PI / 2);
            point.applyAxisAngle(new THREE.Vector3(0, 1, 0), Math.PI / 2);
            point.multiplyScalar(1.35);
            point.add(new THREE.Vector3(0, 0, 1).multiplyScalar(1.35));
            points.push(point);
        }
        const tubeGeometry = new THREE.TubeGeometry(points, 32, 0.05, 10, false);
        const tubeMaterial = new THREE.MeshBasicMaterial({
            color: 0xffa500
        });
        const tubeMesh = new THREE.Mesh(tubeGeometry, tubeMaterial);
        scene.add(tubeMesh);

        const tubeGeometry2 = new THREE.TubeGeometry(points.slice().reverse(), 32, 0.05, 10, false);
        const tubeMaterial2 = new THREE.MeshBasicMaterial({
            color: 0x4169e1
        });
        const tubeMesh2 = new THREE.Mesh(tubeGeometry2, tubeMaterial2);
        scene.add(tubeMesh2);

        let checkboxBoundaryOnly = document.getElementById('checkbox-boundary-only');
        let labelCylinderRadius = document.getElementById('label-cylinder-radius');
        let labelOpacity = document.getElementById('label-opacity');
        let labelDrillRadius = document.getElementById('label-drill-radius');
        let labelInclination = document.getElementById('label-inclination');
        let labelRotation = document.getElementById('label-rotation');
        let labelOffset = document.getElementById('label-offset');

        function updateScene() {
            const cylinderRadius = parseFloat(document.getElementById('slider-cylinder-radius').value);
            const opacity = parseFloat(document.getElementById('slider-opacity').value);
            const drillRadius = parseFloat(document.getElementById('slider-drill-radius').value);
            const inclination = parseFloat(document.getElementById('slider-inclination').value);
            const rotation = parseFloat(document.getElementById('slider-rotation').value);
            const offset = parseFloat(document.getElementById('slider-offset').value);

            cylinderMesh.scale.set(cylinderRadius, cylinderRadius, cylinderRadius);
            drillCylinderMesh.scale.set(drillRadius, drillRadius, drillRadius);
            drillCylinderMesh.rotation.x = inclination;
            drillCylinderMesh.rotation.y = rotation;
            drillCylinderMesh.position.y = offset;

            cylinderMaterial.opacity = opacity;

            if (checkboxBoundaryOnly.checked) {
                cylinderMesh.material.opacity = 0.15;
                tubeMesh.material.opacity = 1;
                tubeMesh2.material.opacity = 1;
            } else {
                tubeMesh.material.opacity = 0;
                tubeMesh2.material.opacity = 0;
            }

            labelCylinderRadius.textContent = cylinderRadius.toFixed(2);
            labelOpacity.textContent = opacity.toFixed(2);
            labelDrillRadius.textContent = drillRadius.toFixed(2);
            labelInclination.textContent = inclination.toFixed(2);
            labelRotation.textContent = rotation.toFixed(2);
            labelOffset.textContent = offset.toFixed(2);
        }

        document.getElementById('slider-cylinder-radius').addEventListener('input', updateScene);
        document.getElementById('slider-opacity').addEventListener('input', updateScene);
        document.getElementById('slider-drill-radius').addEventListener('input', updateScene);
        document.getElementById('slider-inclination').addEventListener('input', updateScene);
        document.getElementById('slider-rotation').addEventListener('input', updateScene);
        document.getElementById('slider-offset').addEventListener('input', updateScene);
        checkboxBoundaryOnly.addEventListener('change', updateScene);

        updateScene();

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        animate();
    </script>
</body>
</html>
