        // Simple linear regression function
        function linearRegression(data) {
            let x = data.map(point => point[0]);
            let y = data.map(point => point[1]);
            let n = x.length;
            let sumX = x.reduce((a, b) => a + b, 0);
            let sumY = y.reduce((a, b) => a + b, 0);
            let sumXSq = x.reduce((a, b) => a + b * b, 0);
            let sumXY = x.reduce((a, b, i) => a + b * y[i], 0);
            let slope = (n * sumXY - sumX * sumY) / (n * sumXSq - sumX * sumX);
            let intercept = (sumY - slope * sumX) / n;
            let rSquared = 1 - (sumY - slope * sumX).reduce((a, b) => a + b * b, 0) / (n * sumY.reduce((a, b) => a + b * b, 0));
            return { slope, intercept, rSquared };
        }

        // Generate data points based on the selected model and slider values
        function generateData(model, n, tmin, tmax, tref, c, b, a) {
            let temperatures = Array.from({ length: n }, (_, i) => tmin + (tmax - tmin) * i / (n - 1));
            let data = [];
            if (model === 'Arrhenius') {
                temperatures.forEach(t => {
                    let y = Math.exp(-a / (t + b)) / Math.exp(-a / (tref + b));
                    data.push([t, y]);
                });
            } else if (model === 'exponential') {
                temperatures.forEach(t => {
                    let y = Math.exp(c * (t - tref));
                    data.push([t, y]);
                });
            }
            return data;
        }

