                let transformedData = data.map(point => [point[0] - data[0][0], Math.log(point[1])]);
                let fit = linearRegression(transformedData);
                let c = fit.slope;
                let rSquared = fit.rSquared;
                return { c, rSquared };
            } else if (model === 'exponential') {
                let transformedData = data.map(point => [1 / (point[0] + b), Math.log(point[1])]);
