            'GettysburgAddress': 'Four score and seven years ago our fathers brought forth on this continent a new nation, conceived in Liberty, and dedicated to the proposition that all men are created equal.'
        };

        let cipherFunctions = {
            'Caesar': (text, offset) => {
                return text.split('').map(c => String.fromCharCode((c.charCodeAt(0) - 97 + offset) % 26 + 97)).join('');
            },
            'De Vigenere': (text, keyword) => {
                let key = keyword.split('').map(c => c.charCodeAt(0) - 97);
                let encrypted = '';
                let i = 0;
                for (let c of text) {
                    let charCode = c.charCodeAt(0) - 97;
                    let keyCharCode = key[i % key.length];
                    encrypted += String.fromCharCode((charCode + keyCharCode) % 26 + 97);
                    i++;
                }
                return encrypted;
            },
            'Autokey': (text, keyword) => {
                let key = keyword.split('').map(c => c.charCodeAt(0) - 97);
                let encrypted = '';
                let i = 0;
                for (let c of text) {
                    let charCode = c.charCodeAt(0) - 97;
                    let keyCharCode = key[i % key.length];
                    encrypted += String.fromCharCode((charCode + keyCharCode) % 26 + 97);
                    key.push(charCode);
                    i++;
                }
                return encrypted;
            }
        };

