            if (cipher === 'Caesar') {
                encryptedTextDisplay = cipherFunctions[cipher](processedText, offset);
                encryptedBars = Object.keys(originalFrequency).map(c => [c, (originalFrequency[c] / processedText.length) * 100]);
                encryptedBars.sort((a, b) => b[1] - a[1]);
            } else if (cipher === 'De Vigenere' || cipher === 'Autokey') {
                encryptedTextDisplay = cipherFunctions[cipher](processedText, keyword);
                encryptedBars = Object.keys(originalFrequency).map(c => [c, (originalFrequency[c] / processedText.length) * 100]);
                encryptedBars.sort((a, b) => b[1] - a[1]);
            } else {
                encryptedTextDisplay = '';
                encryptedBars = [];
            }
            encryptedText.textContent = encryptedTextDisplay;
            let encryptedFrequency = {};
