                if (encryptedFrequency[c]) {
                    encryptedFrequency[c]++;
                } else {
                    encryptedFrequency[c] = 1;
                }
            }
            encryptedBars.sort((a, b) => b[1] - a[1]);
            let encryptedChartConfig = {
                data: [{
