        let plotOption = 'pressure drop';

        function updatePlot() {
            const lambda = Math.pow(10, parseFloat(document.getElementById('slider-lambda').value));
            const omega = Math.pow(10, parseFloat(document.getElementById('slider-omega').value));
            const tauValues = [];
            const yValues = [];

            for (let i = 0; i < 200; i++) {
                const tau = Math.pow(10, i / 5);
                tauValues.push(tau);
                yValues.push(calculatePressureDrop(tau, lambda, omega));
            }

            const plotData = [
                {
                    x: tauValues,
                    y: yValues,
                    mode: 'lines',
                    line: { color: 'red', width: 2 },
                },
            ];

            const layout = {
                title: plotOption === 'pressure drop' ? 'Pressure drawdown at the wellbore' : 'Deviation from the asymptote',
                xaxis: {
                    title: 'Dimensionless time, τ',
                    type: 'log',
                    range: [0, 10],
                    dtick: 2,
                    gridcolor: '#ddd',
                },
                yaxis: {
                    title: plotOption === 'pressure drop' ? 'Dimensionless pressure drop, ΔP' : 'Dimensionless difference, ΔP − 1/2 (ln(τ) + 0.8091)',
                    range: plotOption === 'pressure drop' ? [0, 12] : [0, 3.5],
                    gridcolor: '#ddd',
                },
                margin: { l: 80, r: 50, b: 60, t: 60 },
                paper_bgcolor: '#fff',
                plot_bgcolor: '#fff',
            };

            Plotly.newPlot('plot-container', plotData, layout);
        }

        function calculatePressureDrop(tau, lambda, omega) {
            const laplaceFunc = (s) => {
                const f = (omega * (1 - omega) * s + lambda) / ((1 - omega) * s + lambda);
                const besselK0 = besselK0(Math.sqrt(s * f));
                const besselK1 = besselK1(Math.sqrt(s * f));
                return (K_0(Math.sqrt(s * f)) / (Math.sqrt(s * f) * K_1(Math.sqrt(s * f)))) / s;
            };

            const nTerms = 10;
            const stehfestResult = stehfest(laplaceFunc, tau, nTerms);
            return plotOption === 'pressure drop' ? stehfestResult : stehfestResult - 0.5 * (Math.log(tau) + 0.8091);
        }

        function stehfest(laplaceFunc, t, nTerms) {
            let result = 0;
            for (let i = 1; i <= nTerms; i++) {
                result += (laplaceFunc(i * Math.PI / (2 * t)) * Math.pow(-1, i - 1)) / (Math.pow(i, 2) * Math.pow(Math.PI, 2));
            }
            return result * 2 * t;
        }

        function besselK0(x) {
            return 1 / Math.sqrt(x) * Math.exp(-x);
        }

        function besselK1(x) {
            return 1 / x * Math.exp(-x);
        }

        document.getElementById('slider-lambda').addEventListener('input', () => {
