        function mouseDragged() {
            if (selectedPoint) {
                let x = mouseX;
                let y = mouseY;
                let age = map(x, 0, width, 20, 80);
                let volume = map(y, 0, height, 0, 1500);
                selectedPoint.age = age;
                selectedPoint.volume = volume;
                recalculate();
            }
        }

        function mouseReleased() {
