            for (let i = dataPoints.length - 1; i >= 0; i--) {
                let point = dataPoints[i];
                let x = map(point.age, 20, 80, 0, width);
                let y = map(point.volume, 0, 1500, height, 0);
                if (dist(mouseX, mouseY, x, y) < 5) {
                    dataPoints.splice(i, 1);
                    recalculate();
                    return;
                }
            }
        }

        function recalculate() {
            let learningRate = 0.01;
            let maxIterations = 1000;
            let epsilon = 0.001;
            let E = 0;
            let E_prev = 0;
            let p_new = p;
            let u_new = u;
            for (let i = 0; i < maxIterations; i++) {
                for (let point of dataPoints) {
                    let x = point.age;
                    let y = point.volume;
                    let y_pred = p * Math.pow(x - u, 4/5);
                    E += Math.pow(y - y_pred, 2);
                }
                E /= dataPoints.length;
                E_prev = E;
                p_new -= learningRate * (2 * p * E / dataPoints.length);
                u_new -= learningRate * (2 * u * E / dataPoints.length);
                if (u_new < 20 - epsilon) {
                    u_new = 20 - epsilon;
                }
                p = p_new;
                u = u_new;
                E = 0;
                for (let point of dataPoints) {
                    let x = point.age;
                    let y = point.volume;
                    let y_pred = p * Math.pow(x - u, 4/5);
                    E += Math.pow(y - y_pred, 2);
                }
                E /= dataPoints.length;
                if (Math.abs(E - E_prev) < 0.0001) {
                    break;
                }
            }
            updateUI();
        }

