    </style>
</head>
<body>
    <div id="demo-container">
        <div id="controls">
            <label for="angle-slider">vertices sorted by angle</label>
            <input type="range" id="angle-slider" min="0" max="38" step="1" value="3">
            <span id="angle-value">3</span>
            <button id="reset-button">+</button>
        </div>
        <div id="canvas-container">
            <canvas id="canvas" width="600" height="600"></canvas>
        </div>
    </div>
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
    <script>
        let polygons = [
            [[50, 50], [550, 50], [550, 550], [50, 550]],
            [[100, 100], [250, 100], [250, 150], [200, 150], [200, 200], [100, 200]],
            [[300, 100], [450, 100], [450, 250], [400, 250], [400, 200], [300, 200]],
            [[100, 350], [250, 350], [250, 500], [200, 500], [200, 450], [100, 450]],
            [[300, 400], [450, 400], [450, 500], [300, 500]],
            [[300, 300], [350, 300], [350, 350], [300, 350]]
        ];

        let sourcePoint = { x: 275, y: 275 };
        let angle = 3;

        function setup() {
            createCanvas(600, 600);
            angleMode(DEGREES);
            frameRate(30);
        }

        function draw() {
            background(220);
            drawPolygons();
            drawSourcePoint();
            drawVisibilityPolygon();
            drawHelperGraphics();
        }

        function drawPolygons() {
            fill(255);
            stroke(170);
            strokeWeight(1);
            for (let i = 0; i < polygons.length; i++) {
                beginShape();
                for (let j = 0; j < polygons[i].length; j++) {
                    vertex(polygons[i][j][0], polygons[i][j][1]);
                }
                endShape(CLOSE);
            }
            for (let i = 0; i < polygons.length; i++) {
                for (let j = 0; j < polygons[i].length; j++) {
                    fill(255, 0, 0);
                    ellipse(polygons[i][j][0], polygons[i][j][1], 4);
                }
            }
        }

        function drawSourcePoint() {
            fill(0, 0, 255);
            ellipse(sourcePoint.x, sourcePoint.y, 8);
            stroke(0, 0, 255);
            strokeWeight(1);
            ellipse(sourcePoint.x, sourcePoint.y, 8);
        }

        function drawVisibilityPolygon() {
            let vertices = getSortedVertices();
            let polygon = getVisibilityPolygon(vertices, angle);
            if (angle < 38) {
                fill(255, 255, 153);
                stroke(170, 170, 170, 0.5);
                strokeWeight(1);
                beginShape();
                for (let i = 0; i < polygon.length; i++) {
                    vertex(polygon[i].x, polygon[i].y);
                }
                endShape(CLOSE);
                fill(255, 204, 102);
                stroke(170, 170, 170, 0.5);
                strokeWeight(1);
                beginShape();
                for (let i = 0; i < angle; i++) {
                    vertex(vertices[i].x, vertices[i].y);
                }
                endShape(CLOSE);
            } else {
                fill(255, 204, 102);
                stroke(170, 170, 170, 0.5);
                strokeWeight(1);
                beginShape();
                for (let i = 0; i < polygon.length; i++) {
                    vertex(polygon[i].x, polygon[i].y);
                }
                endShape(CLOSE);
            }
        }

        function drawHelperGraphics() {
            if (angle < 38) {
                let ray = getRay();
                fill(0, 0, 255);
                stroke(0, 0, 255);
                strokeWeight(1);
                line(sourcePoint.x, sourcePoint.y, ray.x, ray.y);
                fill(0, 0, 255);
                stroke(0, 0, 255);
                strokeWeight(1);
                arc(sourcePoint.x, sourcePoint.y, 20, 20, 0, angle);
                let edge = getActiveEdge();
                fill(0, 255, 0);
                stroke(0, 255, 0);
                strokeWeight(3);
                beginShape();
                for (let i = 0; i < edge.length; i++) {
                    vertex(edge[i].x, edge[i].y);
                }
                endShape(CLOSE);
            }
        }

        function getSortedVertices() {
            let vertices = [];
            for (let i = 0; i < polygons.length; i++) {
                for (let j = 0; j < polygons[i].length; j++) {
                    vertices.push({ x: polygons[i][j][0], y: polygons[i][j][1] });
                }
            }
            vertices.sort((a, b) => {
                let angleA = atan2(a.y - sourcePoint.y, a.x - sourcePoint.x);
                let angleB = atan2(b.y - sourcePoint.y, b.x - sourcePoint.x);
                return angleA - angleB;
            });
            return vertices;
        }

        function getVisibilityPolygon(vertices, angle) {
            let polygon = [];
            for (let i = 0; i < angle; i++) {
                let vertex = vertices[i];
                let ray = getRay();
                let intersection = getIntersection(vertex, ray);
                polygon.push(intersection);
            }
            return polygon;
        }

        function getRay() {
            let angle = atan2(sourcePoint.y - vertices[angle].y, sourcePoint.x - vertices[angle].x);
            let rayX = sourcePoint.x + cos(angle) * 100;
            let rayY = sourcePoint.y + sin(angle) * 100;
            return { x: rayX, y: rayY };
        }

        function getIntersection(vertex, ray) {
            let intersectionX = (ray.x - vertex.x) * (vertex.x - sourcePoint.x) / (ray.x - sourcePoint.x) + vertex.x;
            let intersectionY = (ray.y - vertex.y) * (vertex.y - sourcePoint.y) / (ray.y - sourcePoint.y) + vertex.y;
            return { x: intersectionX, y: intersectionY };
        }

        function getActiveEdge() {
            let edge = [];
            for (let i = 0; i < polygons.length; i++) {
                for (let j = 0; j < polygons[i].length; j++) {
                    let vertex1 = polygons[i][j];
                    let vertex2 = polygons[i][(j + 1) % polygons[i].length];
                    let intersection = getIntersection(vertex1, getRay());
                    if (dist(intersection.x, intersection.y, sourcePoint.x, sourcePoint.y) < 100) {
                        edge.push(vertex1);
                        edge.push(vertex2);
                    }
                }
            }
            return edge;
        }

        setup();
        draw();
    </script>
</body>
