</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-container">
                <label class="slider-label" for="slider-order">expansion order</label>
                <input type="range" id="slider-order" min="1" max="5" step="1" value="5">
                <span class="slider-value" id="value-order">5</span>
            </div>
            <div class="slider-container">
                <label class="slider-label" for="slider-a">f(0)</label>
                <input type="range" id="slider-a" min="0.1" max="3.0" step="0.1" value="2.0">
                <span class="slider-value" id="value-a">2.0</span>
            </div>
            <div class="slider-container">
                <label class="slider-label" for="slider-b">f'(0)</label>
                <input type="range" id="slider-b" min="-2.0" max="2.0" step="0.1" value="0.0">
                <span class="slider-value" id="value-b">0.0</span>
            </div>
        </div>
        <div id="visualization-area">
            <div id="plot-container"></div>
        </div>
    </div>

    <script src="https://cdn.plotly.com/plotly-2.13.0.min.js"></script>
    <script>
        // Get elements
        const sliderOrder = document.getElementById('slider-order');
        const sliderA = document.getElementById('slider-a');
        const sliderB = document.getElementById('slider-b');
        const valueOrder = document.getElementById('value-order');
        const valueA = document.getElementById('value-a');
        const valueB = document.getElementById('value-b');
        const plotContainer = document.getElementById('plot-container');

        // Initialize plot
        const layout = {
            title: 'Power Series Approximation and Numerical Solution',
            xaxis: { title: 'x', range: [0, 1.5], zeroline: false },
            yaxis: { title: 'f(x)', range: [0, 4], zeroline: false },
            margin: { l: 50, r: 20, t: 50, b: 40 },
            shapes: []
        };

        const traceRed = {
            type: 'scatter',
            mode: 'lines',
            line: { color: 'red', width: 2 }
        };

        const traceGreen = {
            type: 'scatter',
            mode: 'lines',
            line: { color: 'green', width: 3 }
        };

        Plotly.newPlot(plotContainer, [traceRed, traceGreen], layout);

        // Update function
        function update() {
            // Read inputs
            const order = parseInt(sliderOrder.value);
            const a = parseFloat(sliderA.value);
            const b = parseFloat(sliderB.value);

            // Update value displays
            valueOrder.textContent = order;
            valueA.textContent = a.toFixed(1);
            valueB.textContent = b.toFixed(1);

            // Calculate power series coefficients
            const fDerivs = [a, b];
            for (let k = 2; k <= order; k++) {
                fDerivs[k] = -[fDerivs[k - 1] * fDerivs[k - 2] + fDerivs[k - 2] + 1.5 * fDerivs[k - 1]] / fDerivs[0];
            }

            // Generate power series data
            const xValues = [];
            const yValues = [];
            for (let x = 0; x <= 1.5; x += 0.01) {
                let y = 0;
                for (let k = 0; k <= order; k++) {
                    y += fDerivs[k] / factorial(k) * Math.pow(x, k);
                }
                xValues.push(x);
                yValues.push(y);
            }

            // Generate numerical solution data
            const numericalSolution = [];
            const h = 0.01;
            const y1 = a;
            const y2 = b;
            for (let x = 0; x <= 1.5; x += h) {
                const k1 = y2;
                const k2 = y2 + h * (-y2 - 1.5 * y1) / y1;
                const k3 = y2 + h * (-k1 - 1.5 * y1) / y1;
                const k4 = y2 + h * (-k2 - 1.5 * y1) / y1;
                const y1New = y1 + h / 6 * (k1 + 2 * k2 + 2 * k3 + k4);
                const y2New = y2 + h / 6 * (-k1 + 2 * k2 + 2 * k3 + k4);
                y1 = y1New;
                y2 = y2New;
                numericalSolution.push([x, y1]);
            }

            // Determine agreement region
            let xLimit = 1.5;
            for (let i = 0; i < yValues.length; i++) {
                if (Math.abs(yValues[i] - numericalSolution[i][1]) >= 0.01) {
                    xLimit = numericalSolution[i][0];
                    break;
                }
            }

            // Update plot title
            let title = '';
            for (let k = 0; k <= order; k++) {
                if (fDerivs[k] !== 0) {
                    title += (title === '' ? '' : '+ ') + fDerivs[k].toFixed(5) + (k === 0 ? ' x' : ' x^' + k);
                }
            }
            layout.title = title;

            // Update plot
            const shapes = layout.shapes;
            shapes[0].x1 = xLimit;
            Plotly.react('plot-container', [traceRed, traceGreen], layout);

            // Update plot data
            traceRed.x = xValues;
            traceRed.y = yValues;
            traceGreen.x = numericalSolution.map(x => x[0]);
            traceGreen.y = numericalSolution.map(x => x[1]);
            Plotly.relayout('plot-container', layout);
        }

        // Add event listeners
        sliderOrder.addEventListener('input', update);
        sliderA.addEventListener('input', update);
        sliderB.addEventListener('input', update);

        // Initial update
        update();
    </script>
</body>
</html>
