</head>
<body>
    <div id="main-container">
        <div id="controls">
            <div id="text-control">
                <label for="select-text">text</label>
                <select id="select-text">
                    <option value="To Be Or Not To Be">To Be Or Not To Be</option>
                    <option value="The quick brown fox jumps over the lazy dog">The quick brown fox jumps over the lazy dog</option>
                    <option value="It was the best of times, it was the worst of times">It was the best of times, it was the worst of times</option>
                </select>
            </div>
            <div id="view-controls">
                <label>plot</label>
                <button id="btn-plot-2d" class="active">distribution 2D</button>
                <button id="btn-plot-3d">keyboard 3D</button>
                <label>keyboard</label>
                <button id="btn-keyboard-qwerty" class="active">QWERTY</button>
                <button id="btn-keyboard-dvorak">Dvorak</button>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="visualization-canvas"></canvas>
        </div>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script>
        // Initialize variables
        let selectedText = "To Be Or Not To Be";
        let plotView = "2D";
        let keyboardLayout = "QWERTY";

        // Function to calculate letter frequencies
        function calculateFrequencies(text) {
            let frequencies = {};
            for (let char of text.toLowerCase()) {
                if (char.match(/[a-z]/i)) {
                    frequencies[char] = (frequencies[char] || 0) + 1;
                }
            }
            return frequencies;
        }

        // Function to render the 2D bar chart
        function render2DChart(frequencies) {
            let scene = new THREE.Scene();
            let camera = new THREE.OrthographicCamera(-1, 1, 1, -1, 0.1, 10);
            let renderer = new THREE.WebGLRenderer({
                canvas: document.getElementById("visualization-canvas"),
                antialias: true
            });
            renderer.setSize(800, 600);

            // Create bars and labels
            let barWidth = 0.8;
            let barGap = 0.2;
            let barHeight = 0;
            let maxHeight = 0;
            for (let letter in frequencies) {
                let barGeometry = new THREE.BoxGeometry(barWidth, frequencies[letter], 0.1);
                let barMaterial = new THREE.MeshBasicMaterial({ color: 0x0000ff });
                let barMesh = new THREE.Mesh(barGeometry, barMaterial);
                barMesh.position.x = -0.9 + (letter.charCodeAt(0) - 65) * (barWidth + barGap);
                scene.add(barMesh);

                // Update maximum height
                if (frequencies[letter] > maxHeight) {
                    maxHeight = frequencies[letter];
                }

                // Create label
                let labelGeometry = new THREE.PlaneGeometry(0.1, 0.1);
                let labelMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff });
                let labelMesh = new THREE.Mesh(labelGeometry, labelMaterial);
                labelMesh.position.x = barMesh.position.x;
                labelMesh.position.y = frequencies[letter] + 0.05;
                scene.add(labelMesh);

                // Update bar height
                barHeight = frequencies[letter];
            }

            // Set Y-axis scale
            let yScale = Math.ceil(maxHeight / 50) * 50;
            camera.position.y = yScale + 1;
            let axesHelper = new THREE.AxesHelper(1);
            scene.add(axesHelper);

            // Render scene
            renderer.render(scene, camera);
        }

        // Function to render the 3D keyboard
        function render3DKeyboard(frequencies) {
            let scene = new THREE.Scene();
            let camera = new THREE.PerspectiveCamera(75, 800 / 600, 0.1, 1000);
            let renderer = new THREE.WebGLRenderer({
                canvas: document.getElementById("visualization-canvas"),
                antialias: true
            });
            renderer.setSize(800, 600);

            // Create keyboard layout
            let keyboardLayouts = {
                QWERTY: [
                    ['Q', 'W', 'E', 'R', 'T', 'Y', 'U', 'I', 'O', 'P'],
                    ['A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'L'],
                    ['Z', 'X', 'C', 'V', 'B', 'N', 'M']
                ],
                Dvorak: [
                    ['P', 'Y', 'F', 'G', 'C', 'R', 'L', 'U', 'I', 'O'],
                    ['A', 'E', 'D', 'H', 'T', 'N', 'S', 'Q', 'J', 'K'],
                    ['X', 'B', 'M', 'W', 'V', 'Z']
                ]
            };

            // Create keys
            let keyWidth = 0.5;
            let keyHeight = 0.2;
            let keyGap = 0.1;
            let keyDepth = 0.1;
            let keyPositions = {
                QWERTY: [],
                Dvorak: []
            };
            for (let i = 0; i < keyboardLayouts[keyboardLayout][0].length; i++) {
                let x = -0.4 + i * (keyWidth + keyGap);
                keyPositions.QWERTY.push({ x, y: 0.3, z: 0 });
                keyPositions.Dvorak.push({ x, y: 0.1, z: 0 });
            }
            for (let i = 0; i < keyboardLayouts[keyboardLayout][1].length; i++) {
                let x = -0.4 + i * (keyWidth + keyGap);
                keyPositions.QWERTY.push({ x, y: -0.2, z: 0 });
                keyPositions.Dvorak.push({ x, y: 0, z: 0 });
            }
            for (let i = 0; i < keyboardLayouts[keyboardLayout][2].length; i++) {
                let x = -0.4 + i * (keyWidth + keyGap);
                keyPositions.QWERTY.push({ x, y: -0.5, z: 0 });
                keyPositions.Dvorak.push({ x, y: -0.3, z: 0 });
            }

            // Create key meshes
            for (let letter in frequencies) {
                let keyGeometry = new THREE.CylinderGeometry(keyWidth, keyHeight, keyDepth, 32);
                let keyMaterial = new THREE.MeshStandardMaterial({ color: getKeyboardColor(keyboardLayout, letter) });
                let keyMesh = new THREE.Mesh(keyGeometry, keyMaterial);
                keyMesh.position.x = keyPositions[keyboardLayout].find(pos => pos.x === -0.4 + (letter.charCodeAt(0) - 65) * (keyWidth + keyGap)).x;
                keyMesh.position.y = keyPositions[keyboardLayout].find(pos => pos.x === -0.4 + (letter.charCodeAt(0) - 65) * (keyWidth + keyGap)).y;
                keyMesh.position.z = keyPositions[keyboardLayout].find(pos => pos.x === -0.4 + (letter.charCodeAt(0) - 65) * (keyWidth + keyGap)).z;
                scene.add(keyMesh);

                // Create key label
                let labelGeometry = new THREE.PlaneGeometry(0.1, 0.1);
                let labelMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff });
                let labelMesh = new THREE.Mesh(labelGeometry, labelMaterial);
                labelMesh.position.x = keyMesh.position.x;
                labelMesh.position.y = keyMesh.position.y + 0.05;
                scene.add(labelMesh);

                // Update key position
                let keyHeight = frequencies[letter] > 0 ? frequencies[letter] : 0.01;
                keyMesh.position.y = keyPositions[keyboardLayout].find(pos => pos.x === -0.4 + (letter.charCodeAt(0) - 65) * (keyWidth + keyGap)).y + keyHeight / 2;
            }

            // Set camera position
            camera.position.x = 0;
            camera.position.y = 2;
            camera.position.z = 5;
            camera.lookAt(0, 0, 0);

            // Render scene
            renderer.render(scene, camera);
        }

        // Function to get keyboard color
        function getKeyboardColor(layout, letter) {
            let colors = {
                QWERTY: {
                    top: 0x808000,
                    home: 0x0000ff,
                    bottom: 0x800080
                },
                Dvorak: {
                    top: 0x808000,
                    home: 0x0000ff,
                    bottom: 0x800080
                }
            };
            let row = getRow(layout, letter);
            return colors[layout][row];
        }

        // Function to get row for letter
        function getRow(layout, letter) {
            let rows = {
                QWERTY: [
                    ['Q', 'W', 'E', 'R', 'T', 'Y', 'U', 'I', 'O', 'P'],
                    ['A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'L'],
                    ['Z', 'X', 'C', 'V', 'B', 'N', 'M']
                ],
                Dvorak: [
                    ['P', 'Y', 'F', 'G', 'C', 'R', 'L', 'U', 'I', 'O'],
                    ['A', 'E', 'D', 'H', 'T', 'N', 'S', 'Q', 'J', 'K'],
                    ['X', 'B', 'M', 'W', 'V', 'Z']
                ]
            };
            for (let i = 0; i < rows[layout].length; i++) {
                if (rows[layout][i].includes(letter)) {
                    return i;
                }
            }
        }

        // Function to handle button clicks
        function handleButtonClick(event) {
            let target = event.target;
            if (target.id === "btn-plot-2d") {
                plotView = "2D";
                render2DChart(calculateFrequencies(selectedText));
            } else if (target.id === "btn-plot-3d") {
                plotView = "3D";
                render3DKeyboard(calculateFrequencies(selectedText));
            } else if (target.id === "btn-keyboard-qwerty") {
                keyboardLayout = "QWERTY";
                if (plotView === "3D") {
                    render3DKeyboard(calculateFrequencies(selectedText));
                }
            } else if (target.id === "btn-keyboard-dvorak") {
                keyboardLayout = "Dvorak";
                if (plotView === "3D") {
                    render3DKeyboard(calculateFrequencies(selectedText));
                }
            }
        }

        // Add event listeners to buttons
        document.getElementById("btn-plot-2d").addEventListener("click", handleButtonClick);
        document.getElementById("btn-plot-3d").addEventListener("click", handleButtonClick);
        document.getElementById("btn-keyboard-qwerty").addEventListener("click", handleButtonClick);
        document.getElementById("btn-keyboard-dvorak").addEventListener("click", handleButtonClick);

        // Initialize plot view
        render2DChart(calculateFrequencies(selectedText));
    </script>
</body>
</html>
