    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let isPlaying = false;
        let time = 0;
        let particles = [];
        let p_amp = 0.2;
        let p_wl = 3;
        let s_amp = 0.1;
        let s_wl = 1;
        let trail_length = 5;
        let points = 50;

        function setup() {
            createCanvas(800, 400);
            canvas.parent('canvas-container');
            noStroke();
            stroke('#D3D3D3');
            strokeWeight(1);
            fill('#6A5ACD');
            rectMode(CORNER);
            frameRate(30);

            // Initialize particles
            for (let i = 0; i < points; i++) {
                let x = map(i, 0, points - 1, 0, 5);
                particles.push({
                    x: x,
                    y: 0,
                    trail: []
                });
            }

            // Event listeners
            document.getElementById('slider-time').addEventListener('input', () => {
                document.getElementById('label-time').textContent = document.getElementById('slider-time').value;
                if (isPlaying) {
                    time = parseFloat(document.getElementById('slider-time').value);
                }
            });

            document.getElementById('slider-trail').addEventListener('input', () => {
                document.getElementById('label-trail').textContent = document.getElementById('slider-trail').value;
                trail_length = parseInt(document.getElementById('slider-trail').value);
            });

            document.getElementById('slider-points').addEventListener('input', () => {
                document.getElementById('label-points').textContent = document.getElementById('slider-points').value;
                points = parseInt(document.getElementById('slider-points').value);
                particles = [];
                for (let i = 0; i < points; i++) {
                    let x = map(i, 0, points - 1, 0, 5);
                    particles.push({
                        x: x,
                        y: 0,
                        trail: []
                    });
                }
            });

            document.getElementById('btn-play-pause').addEventListener('click', () => {
                isPlaying = !isPlaying;
                if (isPlaying) {
                    document.getElementById('btn-play-pause').textContent = 'Pause';
                } else {
                    document.getElementById('btn-play-pause').textContent = 'Play';
                }
            });

            document.getElementById('btn-reset').addEventListener('click', () => {
                isPlaying = false;
                document.getElementById('btn-play-pause').textContent = 'Play';
                time = 0;
                document.getElementById('slider-time').value = 0;
                document.getElementById('label-time').textContent = 0;
                p_amp = 0.2;
                p_wl = 3;
                s_amp = 0.1;
                s_wl = 1;
                document.getElementById('slider-p-amp').value = 0.2;
                document.getElementById('slider-p-wl').value = 3;
                document.getElementById('slider-s-amp').value = 0.1;
                document.getElementById('slider-s-wl').value = 1;
                document.getElementById('label-p-amp').textContent = 0.2;
                document.getElementById('label-p-wl').textContent = 3;
                document.getElementById('label-s-amp').textContent = 0.1;
                document.getElementById('label-s-wl').textContent = 1;
                particles = [];
                for (let i = 0; i < points; i++) {
                    let x = map(i, 0, points - 1, 0, 5);
                    particles.push({
                        x: x,
                        y: 0,
                        trail: []
                    });
                }
                background('#FFFFFF');
            });

            document.getElementById('slider-p-amp').addEventListener('input', () => {
                p_amp = parseFloat(document.getElementById('slider-p-amp').value);
                document.getElementById('label-p-amp').textContent = p_amp;
            });

            document.getElementById('slider-p-wl').addEventListener('input', () => {
                p_wl = parseFloat(document.getElementById('slider-p-wl').value);
                document.getElementById('label-p-wl').textContent = p_wl;
            });

            document.getElementById('slider-s-amp').addEventListener('input', () => {
                s_amp = parseFloat(document.getElementById('slider-s-amp').value);
                document.getElementById('label-s-amp').textContent = s_amp;
            });

            document.getElementById('slider-s-wl').addEventListener('input', () => {
                s_wl = parseFloat(document.getElementById('slider-s-wl').value);
                document.getElementById('label-s-wl').textContent = s_wl;
            });
        }

        function draw() {
            background('#FFFFFF');

            // Draw axes
            stroke('#000');
            line(0, height / 2, width, height / 2);
            line(width / 2, 0, width / 2, height);

            // Draw particles
            for (let i = 0; i < particles.length; i++) {
                let x = particles[i].x + sin(2 * PI * (particles[i].x / p_wl - time)) * p_amp;
                let y = sin(2 * PI * (particles[i].x / s_wl - time)) * s_amp;

                // Draw trail
                for (let j = 0; j < trail_length; j++) {
                    if (j < particles[i].trail.length) {
                        let trail_x = particles[i].trail[j].x;
                        let trail_y = particles[i].trail[j].y;
                        fill('#66CCCC');
                        noStroke();
                        ellipse(trail_x, trail_y, 2);
                    }
                }

                // Draw particle
                fill('#6A5ACD');
                ellipse(x, y, 10);

                // Update particle position
                particles[i].trail.push({ x: x, y: y });
                if (particles[i].trail.length > trail_length) {
                    particles[i].trail.shift();
                }
            }

            // Update time label
            document.getElementById('label-time').textContent = time;

            // Increment time if playing
            if (isPlaying) {
                time = (time + 0.01) % 5;
            }
        }

        setup();
        draw();
    </script>
</body>
</html>
