                y: { show: true, color: 'black', width: 0.5 },
                z: { show: false }
            },
            lighting: {
                ambient: 0.8,
                diffuse: 0.8,
                specular: 0.2
            }
        };

        let layout = {
            title: '',
            scene: {
                xaxis: { title: 'x (m)', range: [-1000, 1000] },
                yaxis: { title: 'y (m)', range: [-1000, 1000] },
                zaxis: { title: 'z (mm)', autorange: true },
                camera: { eye: { x: 1.8, y: 1.8, z: 0.8 } }
            },
            margin: { l: 20, r: 20, b: 20, t: 20 }
        };

        Plotly.newPlot('plot', plotData, layout);

        // Add event listeners to sliders and button groups
        document.getElementById('slider-dip').addEventListener('input', updatePlot);
        document.getElementById('slider-depth').addEventListener('input', updatePlot);
        document.getElementById('slider-limit').addEventListener('input', updatePlot);

        document.getElementById('btn-group-component').addEventListener('click', (e) => {
            if (e.target.tagName === 'BUTTON') {
                let component = e.target.id.replace('btn-component-', '');
                document.getElementById('btn-component-x').classList.remove('active');
                document.getElementById('btn-component-y').classList.remove('active');
                document.getElementById('btn-component-z').classList.remove('active');
                e.target.classList.add('active');
                updatePlot();
            }
        });

        document.getElementById('btn-group-fault-type').addEventListener('click', (e) => {
            if (e.target.tagName === 'BUTTON') {
                let faultType = e.target.id.replace('btn-fault-', '');
                document.getElementById('btn-fault-tensile').classList.remove('active');
                document.getElementById('btn-fault-strike-slip').classList.remove('active');
                document.getElementById('btn-fault-normal').classList.remove('active');
                e.target.classList.add('active');
                updatePlot();
            }
        });

        // Function to update plot based on new values
        function updatePlot() {
            let dip = parseFloat(document.getElementById('slider-dip').value);
            let depth = parseFloat(document.getElementById('slider-depth').value);
            let limit = parseFloat(document.getElementById('slider-limit').value);
            let component = document.querySelector('#btn-group-component button.active').id.replace('btn-component-', '');
            let faultType = document.querySelector('#btn-group-fault-type button.active').id.replace('btn-fault-', '');

            let xCoords = [];
            let yCoords = [];
            let zValues = [];

            for (let i = -limit; i <= limit; i += (limit / 50)) {
                xCoords.push(i);
                yCoords.push(i);
                let zValue = 0;
                if (component === 'x') {
                    zValue = calculateDisplacements(i, 0, dip, depth, faultType, 0.25).ux * 1000;
                } else if (component === 'y') {
                    zValue = calculateDisplacements(0, i, dip, depth, faultType, 0.25).uy * 1000;
                } else {
                    zValue = calculateDisplacements(i, 0, dip, depth, faultType, 0.25).uz * 1000;
                }
                zValues.push(zValue);
            }

            plotData.x = xCoords;
            plotData.y = yCoords;
            plotData.z = zValues;

            Plotly.react('plot', [plotData], layout);
        }

        // Function to calculate surface displacement
        function calculateDisplacements(x, y, dip, depth, faultType, nu) {
            let b = 1; // Dislocation magnitude in meters
            let dislocationAngle = Math.atan(Math.tan(dip) * (depth / (depth + Math.sqrt(x * x + y * y))));
            let ux, uy, uz;

            if (faultType === 'tensile') {
                ux = -b * Math.sin(dislocationAngle);
                uy = 0;
                uz = 0;
            } else if (faultType === 'strike-slip') {
                ux = 0;
                uy = -b * Math.cos(dislocationAngle);
                uz = 0;
            } else {
                ux = 0;
                uy = 0;
                uz = b * Math.sin(dislocationAngle);
            }

            return { ux, uy, uz };
        }
    </script>
