    <script>
        // Define the Bessel function of the first kind of order zero
        function besselJ0(x) {
            // Polynomial approximation of J0(x) for small x
            if (x < 3.14) {
                return 1 - (x * x) / 2 + (x * x * x * x) / 24 - (x * x * x * x * x * x) / 720;
            } else {
                // For larger x, use a more accurate approximation or a lookup table
                return 0; // placeholder for larger x values
            }
        }

        // Pre-calculate the first 10 zeros of the Bessel function J0(x)
        const zeros = [2.4048, 5.5201, 8.6537, 11.7915, 14.9309, 18.0711, 21.2116, 24.3525, 27.4935, 30.6346];

        let t = 0.1;
        let k = 2;
        let omega_k = zeros[k - 1] / 2;
        let amplitude = 0.8;

        // Set up the p5.js canvas
        let canvas;
        function setup() {
            canvas = createCanvas(600, 400);
            canvas.parent('canvas-container');
            frameRate(30);
        }

        // Draw the string on the canvas
        function draw() {
            background(240);
            if (document.getElementById('checkbox-axes').checked) {
                // Draw axes
                stroke(0);
                line(0, height / 2, width, height / 2);
                line(width / 2, 0, width / 2, height);
                for (let i = -2; i <= 2; i++) {
                    line(width / 2, height / 2 - 20 * (i + 2), width / 2, height / 2 + 20 * (i + 2));
                    line(width / 2 - 20 * (i + 2), height / 2, width / 2 + 20 * (i + 2), height / 2);
                    fill(0);
                    textSize(16);
                    textAlign(CENTER, CENTER);
                    text(i, width / 2, height / 2 - 25 * (i + 2));
                    text(i, width / 2 - 25 * (i + 2), height / 2);
                }
            }
            // Calculate parameters
            let timeFactor = cos(omega_k * t);
            let z_k = zeros[k - 1];
            // Draw the string
            stroke(0, 0, 255);
            noFill();
            beginShape();
            for (let y_norm = 0; y_norm <= 1; y_norm += 0.01) {
                let bessel_arg = z_k * sqrt(y_norm);
                let x_pos = amplitude * timeFactor * besselJ0(bessel_arg);
                let y_pos = -y_norm;
                vertex(map(x_pos, -2, 2, 0, width), map(y_pos, 0, 1, height / 2, height / 2 + 120));
            }
            endShape();
        }

        // Update the formula display when k changes
        document.getElementById('slider-k').addEventListener('input', () => {
            k = parseInt(document.getElementById('slider-k').value);
            omega_k = zeros[k - 1] / 2;
            document.getElementById('k-value-display').textContent = k;
            document.getElementById('formula-display').innerHTML = `\\(\\omega_\\${k} = \\${omega_k.toFixed(2)} \\sqrt{g/l}\\)`;
        });

        // Update the formula display when t changes
        document.getElementById('slider-t').addEventListener('input', () => {
            t = parseFloat(document.getElementById('slider-t').value);
            document.getElementById('t-value-display').textContent = t.toFixed(2);
        });

        // Update the formula display when the checkbox changes
        document.getElementById('checkbox-axes').addEventListener('change', () => {
            if (document.getElementById('checkbox-axes').checked) {
                document.getElementById('formula-display').style.display = 'block';
            } else {
                document.getElementById('formula-display').style.display = 'none';
            }
        });

        // Initialize p5.js
        setup();

        // Set up the draw loop
        draw();
    </script>
</body>
