    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let layeringOrderIndex = 0;
        let layeringPermutations = [[1, 2, 3, 4], [4, 3, 2, 1], [1, 3, 2, 4], [2, 4, 1, 3], [3, 1, 4, 2]];
        let circleData = [
            {x: 150, y: 150, diameter: 100, label: '1', color: '#00ff00', opacity: 0.7},
            {x: 350, y: 150, diameter: 100, label: '2', color: '#0000ff', opacity: 0.7},
            {x: 250, y: 250, diameter: 80, label: '3', color: '#ff0000', opacity: 0.7},
            {x: 250, y: 200, diameter: 60, label: '4', color: '#000000', opacity: 0.7},
        ];

        let p5Instance;
        let canvas;

        function setup() {
            canvas = createCanvas(600, 400);
            canvas.parent('canvas-container');
            p5Instance = createInstance(canvas);
            p5Instance.setup = function() {
                background(220);
                noLoop();
            };
            p5Instance.draw = function() {
                background(220);
                let currentOrder = layeringPermutations[layeringOrderIndex];
                for (let i = 0; i < currentOrder.length; i++) {
                    let circleIndex = currentOrder[i] - 1;
                    let circle = circleData[circleIndex];
                    let opacity = sliderOpacity(circleIndex);
                    let color = colorPicker(circleIndex);
                    fill(color.r, color.g, color.b, opacity * 255);
                    noStroke();
                    ellipse(circle.x, circle.y, circle.diameter);
                    fill(0);
                    textSize(32);
                    textAlign(CENTER, CENTER);
                    text(circle.label, circle.x, circle.y);
                }
            };
        }

        function createInstance(canvas) {
            return new p5(function(sketch) {
                sketch.setup = function() {
                    sketch.createCanvas(600, 400);
                    sketch.background(220);
                    sketch.noLoop();
                };
                sketch.draw = function() {
                    sketch.background(220);
                    let currentOrder = layeringPermutations[layeringOrderIndex];
                    for (let i = 0; i < currentOrder.length; i++) {
                        let circleIndex = currentOrder[i] - 1;
                        let circle = circleData[circleIndex];
                        let opacity = sliderOpacity(circleIndex);
                        let color = colorPicker(circleIndex);
                        sketch.fill(color.r, color.g, color.b, opacity * 255);
                        sketch.noStroke();
                        sketch.ellipse(circle.x, circle.y, circle.diameter);
                        sketch.fill(0);
                        sketch.textSize(32);
                        sketch.textAlign(sketch.CENTER, sketch.CENTER);
                        sketch.text(circle.label, circle.x, circle.y);
                    }
                };
            }, canvas);
        }

        function sliderOpacity(index) {
            let slider = document.getElementById(`slider-opacity${index + 1}`);
            return parseFloat(slider.value);
        }

        function colorPicker(index) {
            let picker = document.getElementById(`color-picker${index + 1}`);
            let hexColor = picker.value;
            let color = hexToRgb(hexColor);
            return color;
        }

        function hexToRgb(hex) {
            let result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
            return result ? {
                r: parseInt(result[1], 16),
                g: parseInt(result[2], 16),
                b: parseInt(result[3], 16)
            } : null;
        }

        document.getElementById('slider-opacity1').addEventListener('input', function() {
            redraw();
        });
        document.getElementById('slider-opacity2').addEventListener('input', function() {
            redraw();
        });
        document.getElementById('slider-opacity3').addEventListener('input', function() {
            redraw();
        });
        document.getElementById('slider-opacity4').addEventListener('input', function() {
            redraw();
        });
        document.getElementById('color-picker1').addEventListener('input', function() {
            redraw();
        });
        document.getElementById('color-picker2').addEventListener('input', function() {
            redraw();
        });
        document.getElementById('color-picker3').addEventListener('input', function() {
            redraw();
        });
        document.getElementById('color-picker4').addEventListener('input', function() {
            redraw();
        });

        document.getElementById('btn-layer-prev').addEventListener('click', function() {
            layeringOrderIndex = (layeringOrderIndex - 1 + layeringPermutations.length) % layeringPermutations.length;
            document.getElementById('layering-display').textContent = layeringPermutations[layeringOrderIndex].join(', ');
            redraw();
        });

        document.getElementById('btn-layer-next').addEventListener('click', function() {
            layeringOrderIndex = (layeringOrderIndex + 1) % layeringPermutations.length;
            document.getElementById('layering-display').textContent = layeringPermutations[layeringOrderIndex].join(', ');
            redraw();
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            document.getElementById('slider-opacity1').value = 0.7;
            document.getElementById('slider-opacity2').value = 0.7;
