    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        const eosButtonSRK = document.getElementById('btn-srk');
        const eosButtonPR = document.getElementById('btn-pr');
        const aspenCheckbox = document.getElementById('checkbox-aspen');
        const sliderPressure = document.getElementById('slider-pressure');
        const displayPressure = document.getElementById('display-pressure');
        const plotContainer = document.getElementById('plot-container');
        let selectedEos = 'SRK';

        eosButtonSRK.classList.add('selected');
        eosButtonPR.classList.remove('selected');

        eosButtonSRK.addEventListener('click', () => {
            selectedEos = 'SRK';
            eosButtonSRK.classList.add('selected');
            eosButtonPR.classList.remove('selected');
            plotFugacityCoefficient();
        });

        eosButtonPR.addEventListener('click', () => {
            selectedEos = 'PR';
            eosButtonPR.classList.add('selected');
            eosButtonSRK.classList.remove('selected');
            plotFugacityCoefficient();
        });

        aspenCheckbox.addEventListener('change', () => {
            const pressure = parseFloat(sliderPressure.value);
            const isAspenVisible = pressure === 10 && aspenCheckbox.checked;
            plotContainer.innerHTML = '';
            plotFugacityCoefficient(isAspenVisible);
        });

        sliderPressure.addEventListener('input', () => {
            displayPressure.textContent = sliderPressure.value;
            const pressure = parseFloat(sliderPressure.value);
            plotContainer.innerHTML = '';
            plotFugacityCoefficient();
        });

        function plotFugacityCoefficient(isAspenVisible = false) {
            const pressure = parseFloat(sliderPressure.value);
            const temperatures = Array.from({ length: 201 }, (_, i) => 300 + i * (500 - 300) / 200);
            const fugacityCoefficients = temperatures.map((temperature) => {
                const Z = calculateCompressibilityFactor(temperature, pressure, selectedEos);
                return Math.exp(Z - 1 - Math.log(Z - 0.08664 * 0.0831446 * temperature / 46.65) - (0.42748 * 0.0831446 * 365.57 * 365.57 / 46.65) / (0.0831446 * temperature * temperature) * Math.log(1 + 0.08664 * 0.0831446 * temperature / (Z + 0.08664 * 0.0831446 * temperature)));
            });
            const plotData = [
                {
                    x: temperatures,
                    y: fugacityCoefficients,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'red', width: 2 },
                },
            ];
            if (isAspenVisible) {
                const aspenData = [
                    { x: [300, 310, 320, 330, 340, 350, 360, 370, 380, 390, 400], y: [0.868, 0.881, 0.891, 0.900, 0.909, 0.917, 0.924, 0.930, 0.937, 0.942, 0.946], type: 'scatter', mode: 'markers', marker: { color: 'green', symbol: 'square', size: 8 } },
                ];
                plotData.push(...aspenData);
            }
            const layout = {
                title: 'Gas-phase Fugacity Coefficient',
                xaxis: { title: 'Temperature (K)' },
                yaxis: { title: 'Fugacity Coefficient' },
                grid: { rows: 20, columns: 20 },
                plot_bgcolor: '#f0f0f0',
                paper_bgcolor: '#f0f0f0',
            };
            Plotly.newPlot(plotContainer, plotData, layout);
        }

        function calculateCompressibilityFactor(temperature, pressure, eos) {
            const Tc = 365.57;
            const Pc = 46.65 * 100;
            const R = 0.0831446;
            const omega = 0.144;
            const Tr = temperature / Tc;
            let a, b, m, alpha, Ae, Be;
            if (eos === 'SRK') {
                a = 0.42748 * R * R * Tc * Tc / Pc;
                b = 0.08664 * R * Tc / Pc;
                m = 0.480 + 1.574 * omega - 0.176 * omega * omega;
                alpha = (1 + m * (1 - Math.sqrt(Tr))) * (1 + m * (1 - Math.sqrt(Tr)));
                Ae = a * alpha;
                Be = b * pressure / (R * temperature);
                const equation = (Z) => Z * Z * Z - Z * Z + (Ae - Be - Be * Be) * Z - Ae * Be;
                const roots = solveCubicEquation(equation);
                return Math.max(...roots);
            } else if (eos === 'PR') {
                a = 0.45724 * R * R * Tc * Tc / Pc;
                b = 0.07780 * R * Tc / Pc;
                m = 0.37464 + 1.54226 * omega - 0.26992 * omega * omega;
                alpha = (1 + m * (1 - Math.sqrt(Tr))) * (1 + m * (1 - Math.sqrt(Tr)));
                Ae = a * alpha;
                Be = b * pressure / (R * temperature);
                const equation = (Z) => Z * Z * Z - (1 - Be) * Z * Z + (Ae - 3 * Be * Be - 2 * Be) * Z - (Ae * Be - Be * Be - Be * Be * Be);
                const roots = solveCubicEquation(equation);
                return Math.max(...roots);
            }
        }

        function solveCubicEquation(equation) {
            const coefficients = [1, -equation(0), equation(0) + equation(0), -equation(0) * equation(0)];
            const discriminant = coefficients[1] * coefficients[1] - 3 * coefficients[0] * coefficients[2];
            if (discriminant < 0) {
                const sqrtDiscriminant = Math.sqrt(-discriminant);
                const sqrtThree = Math.sqrt(3);
                const realRoot = (-coefficients[1] + sqrtDiscriminant) / (3 * coefficients[0]);
                const complexRoot1 = realRoot + sqrtThree / 3;
                const complexRoot2 = realRoot - sqrtThree / 3;
                return [realRoot, complexRoot1, complexRoot2];
            } else if (discriminant === 0) {
                const sqrtDiscriminant = Math.sqrt(discriminant);
                const realRoot1 = (-coefficients[1] + sqrtDiscriminant) / (3 * coefficients[0]);
                const realRoot2 = (-coefficients[1] - sqrtDiscriminant) / (3 * coefficients[0]);
                return [realRoot1, realRoot2];
            } else {
                const sqrtDiscriminant = Math.sqrt(discriminant);
                const sqrtThree = Math.sqrt(3);
                const realRoot1 = (-coefficients[1] + sqrtDiscriminant) / (3 * coefficients[0]);
                const realRoot2 = (-coefficients[1] - sqrtDiscriminant) / (3 * coefficients[0]);
                const complexRoot1 = realRoot1 + sqrtThree / 3;
                const complexRoot2 = realRoot2 + sqrtThree / 3;
                const complexRoot3 = realRoot1 - sqrtThree / 3;
                const complexRoot4 = realRoot2 - sqrtThree / 3;
                return [realRoot1, realRoot2, complexRoot1, complexRoot2, complexRoot3, complexRoot4];
            }
        }
    </script>
