            fill(0);
            rect(100, 100, 50, 50);
            rect(200, 100, 50, 50);
            // Draw bonds
            stroke(0);
            line(100, 100, 150, 150);
            line(150, 150, 200, 100);
            // Draw products
            fill(255, 0, 0);
            rect(300, 100, 50, 50);
            rect(400, 100, 50, 50);
        }

        function drawRegioselectivity() {
            // Draw reactants
            fill(0);
            rect(100, 100, 50, 50);
            rect(200, 100, 50, 50);
            // Draw products
            fill(255, 0, 0);
            rect(300, 100, 50, 50);
            rect(400, 100, 50, 50);
        }

        function drawStereospecificityDienophile() {
            // Draw reactants
            fill(0);
            rect(100, 100, 50, 50);
            rect(200, 100, 50, 50);
            // Draw products
            fill(255, 0, 0);
            rect(300, 100, 50, 50);
        }

        function drawStereospecificityDiene() {
            // Draw reactants
            fill(0);
            rect(100, 100, 50, 50);
            rect(200, 100, 50, 50);
            // Draw products
            fill(255, 0, 0);
            rect(300, 100, 50, 50);
        }

        function drawHOMO_LUMO_interactions() {
            // Draw energy levels
            fill(0);
            rect(100, 100, 50, 50);
            rect(200, 100, 50, 50);
            // Draw interactions
            stroke(255, 0, 0);
            line(100, 150, 200, 150);
        }

        function drawInverse() {
            if (currentState.inverse) {
                fill(255, 0, 0);
                text('diene EWG', 100, 150);
                text('dienophile ERG', 200, 150);
            } else {
                fill(0);
                text('diene ERG', 100, 150);
                text('dienophile EWG', 200, 150);
            }
