        function drawReactionEvolution() {
            let reactionEvolution = currentState.reactionEvolution;
            if (reactionEvolution < 50) {
                fill(255, 0, 0);
                rect(300, 100, 50, 50);
            } else {
                fill(0);
                rect(300, 100, 50, 50);
            }
            if (reactionEvolution > 50) {
                fill(255, 0, 0);
                rect(400, 100, 50, 50);
