<body>
    <div id="demo-container">
        <!-- Section 1: Control Panel -->
        <div id="control-panel">
            <label>step</label>
            <input type="radio" id="step1" name="step-selector" value="1" checked>
            <label for="step1">1</label>
            <input type="radio" id="step2" name="step-selector" value="2">
            <label for="step2">2</label>
            <input type="radio" id="step3" name="step-selector" value="3">
            <label for="step3">3</label>
        </div>
        <!-- Section 2: Visualization Area -->
        <div id="canvas-container">
            <!-- p5.js canvas will be created here -->
            <canvas id="p5-canvas"></canvas>
        </div>
    </div>
    <script>
        let currentStep = 1;
        let p5Canvas;
        let p;

        function setup() {
            p5Canvas = createCanvas(600, 520);
            p5Canvas.parent('canvas-container');
            angleMode(DEGREES);
            background(220);
            rectMode(CENTER);
            noStroke();
            strokeWeight(2);
            stroke(0);
            fill(255);
            p = {
                x: width * 0.4,
                y: height * 0.6
            };
        }

        function draw() {
            background(220);
            drawMainTriangle();
            drawPointP();
            drawMainAltitude();
            drawPerpendiculars();
            drawTriangles();
            drawTranslatedTriangles();
        }

        function drawMainTriangle() {
            triangle(width / 2, height / 2, width / 2 + 200, height / 2, width / 2, height / 2 + 173);
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text('A', width / 2, height / 2 - 20);
            text('B', width / 2 + 200, height / 2 + 20);
            text('C', width / 2, height / 2 + 173 + 20);
        }

        function drawPointP() {
            fill(128);
            ellipse(p.x, p.y, 10);
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text('P', p.x + 5, p.y - 5);
        }

        function drawMainAltitude() {
            line(width / 2, height / 2, width / 2, height / 2 + 173);
            stroke(128);
            strokeWeight(1);
            noFill();
            beginShape();
            vertex(width / 2, height / 2 + 173);
            vertex(width / 2 - 10, height / 2 + 173);
            vertex(width / 2 - 10, height / 2 + 173 + 10);
            vertex(width / 2, height / 2 + 173 + 10);
            endShape(CLOSE);
        }

        function drawPerpendiculars() {
            if (currentStep === 1) {
                drawPerpendicularAB();
                drawPerpendicularBC();
                drawPerpendicularAC();
            }
        }

        function drawPerpendicularAB() {
            let d_AB = dist(p.x, p.y, width / 2, height / 2);
            let footX = p.x - (p.y - height / 2) * width / (height / 2);
            let footY = height / 2;
            line(p.x, p.y, footX, footY);
            fill(0, 0, 255, 128);
            ellipse(footX, footY, 10);
        }

        function drawPerpendicularBC() {
            let d_BC = dist(p.x, p.y, width / 2 + 200, height / 2);
            let footX = width / 2 + 200 + (p.y - height / 2) * width / (height / 2);
            let footY = height / 2;
            line(p.x, p.y, footX, footY);
            fill(0, 0, 255, 128);
            ellipse(footX, footY, 10);
        }

        function drawPerpendicularAC() {
            let d_AC = dist(p.x, p.y, width / 2, height / 2 + 173);
            let footX = width / 2;
            let footY = height / 2 + 173 - (p.x - width / 2) * height / 200;
            line(p.x, p.y, footX, footY);
            fill(0, 0, 255, 128);
            ellipse(footX, footY, 10);
        }

        function drawTriangles() {
            if (currentStep === 2) {
                drawRedTriangle();
                drawGreenTriangle();
                drawBlueTriangle();
            }
        }

        function drawRedTriangle() {
            let d_AB = dist(p.x, p.y, width / 2, height / 2);
            let side = d_AB * 2 / sqrt(3);
            let baseX = width / 2;
            let baseY = height / 2;
            let heightY = height / 2 + 173;
            let apexX = baseX + (heightY - baseY) * (width / (height / 2));
            let apexY = heightY;
            fill(255, 0, 0, 128);
            triangle(baseX, baseY, apexX, apexY, baseX - side / 2, baseY);
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text('x', baseX - side / 2, baseY + 10);
        }

        function drawGreenTriangle() {
            let d_AC = dist(p.x, p.y, width / 2, height / 2 + 173);
            let side = d_AC * 2 / sqrt(3);
            let baseX = width / 2;
            let baseY = height / 2 + 173;
            let heightY = height / 2;
            let apexX = baseX + (baseY - heightY) * (width / (height / 2));
            let apexY = heightY;
            fill(0, 255, 0, 128);
            triangle(baseX, baseY, apexX, apexY, baseX + side / 2, baseY);
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text('y', baseX + side / 2, baseY + 10);
        }

        function drawBlueTriangle() {
            let d_BC = dist(p.x, p.y, width / 2 + 200, height / 2);
            let side = d_BC * 2 / sqrt(3);
            let baseX = width / 2 + 200;
            let baseY = height / 2;
            let heightY = height / 2 + 173;
            let apexX = baseX + (heightY - baseY) * (width / (height / 2));
            let apexY = heightY;
            fill(0, 0, 255, 128);
            triangle(baseX, baseY, apexX, apexY, baseX + side / 2, baseY);
        }

        function drawTranslatedTriangles() {
            if (currentStep === 3) {
                drawRedTranslatedTriangle();
                drawBlueTranslatedTriangle();
                drawGreenTranslatedTriangle();
            }
        }

        function drawRedTranslatedTriangle() {
            let d_AB = dist(p.x, p.y, width / 2, height / 2);
            let side = d_AB * 2 / sqrt(3);
            let baseX = width / 2;
            let baseY = height / 2;
            let heightY = height / 2 + 173;
            let apexX = baseX + (heightY - baseY) * (width / (height / 2));
            let apexY = heightY;
            fill(255, 0, 0, 128);
            push();
            translate(baseX, baseY);
            rotate(-45);
            triangle(0, 0, side / 2, 0, 0, side);
            pop();
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text('x', baseX - side / 2, baseY + 10);
        }

        function drawBlueTranslatedTriangle() {
            let d_BC = dist(p.x, p.y, width / 2 + 200, height / 2);
            let side = d_BC * 2 / sqrt(3);
            let baseX = width / 2 + 200;
            let baseY = height / 2;
            let heightY = height / 2 + 173;
            let apexX = baseX + (heightY - baseY) * (width / (height / 2));
            let apexY = heightY;
            fill(0, 0, 255, 128);
            push();
            translate(baseX, baseY);
            rotate(45);
            triangle(0, 0, side / 2, 0, 0, side);
            pop();
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text('y', baseX + side / 2, baseY + 10);
        }

        function drawGreenTranslatedTriangle() {
            let d_AC = dist(p.x, p.y, width / 2, height / 2 + 173);
            let side = d_AC * 2 / sqrt(3);
            let baseX = width / 2;
            let baseY = height / 2 + 173;
            let heightY = height / 2;
            let apexX = baseX + (baseY - heightY) * (width / (height / 2));
            let apexY = heightY;
            fill(0, 255, 0, 128);
            push();
            translate(baseX, baseY);
            rotate(0);
            triangle(0, 0, side / 2, 0, 0, side);
            pop();
        }

        function mouseDragged() {
            p.x = mouseX;
            p.y = mouseY;
            if (currentStep === 1) {
                drawPerpendicularAB();
                drawPerpendicularBC();
                drawPerpendicularAC();
            } else if (currentStep === 2) {
                drawRedTriangle();
                drawGreenTriangle();
                drawBlueTriangle();
            } else if (currentStep === 3) {
                drawRedTranslatedTriangle();
                drawBlueTranslatedTriangle();
                drawGreenTranslatedTriangle();
            }
        }

        function keyPressed() {
            if (key === '1') {
                currentStep = 1;
            } else if (key === '2') {
                currentStep = 2;
            } else if (key === '3') {
                currentStep = 3;
            }
            redraw();
        }

        setup();
        draw();
    </script>
</body>
</html>
