    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script>
        // Initialization
        let scene, camera, renderer, controls, selectedDwarfPlanet, isAnimating = false;
        let dwarfPlanetOrbitPaths = {};
        let planets = {
            'Mercury': {a: 0.3871, e: 0.2056, i: 7.005, Ω: 48.331, ω: 29.124, M: 174.796},
            'Venus': {a: 0.7233, e: 0.0068, i: 3.395, Ω: 76.680, ω: 54.884, M: 50.416},
            'Earth': {a: 1.0000, e: 0.0167, i: 0.000, Ω: -11.260, ω: 114.208, M: 358.617},
            'Mars': {a: 1.5237, e: 0.0934, i: 1.850, Ω: 49.562, ω: 286.502, M: 19.412},
            'Ceres': {a: 2.7691, e: 0.0760, i: 10.593, Ω: 80.393, ω: 73.598, M: 77.372},
            'Pluto': {a: 39.482, e: 0.2488, i: 17.160, Ω: 110.299, ω: 113.834, M: 14.882},
            'Haumea': {a: 43.132, e: 0.1912, i: 28.220, Ω: 122.155, ω: 239.544, M: 204.03},
            'Makemake': {a: 45.791, e: 0.1559, i: 29.006, Ω: 79.623, ω: 294.821, M: 95.895},
            'Eris': {a: 67.668, e: 0.4418, i: 44.187, Ω: 35.951, ω: 151.291, M: 206.51},
        };

        // Initialization of the scene
        function initScene() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.set(0, 15, 30);

            controls = new THREE.OrbitControls(camera, document.getElementById('3d-canvas'));

            renderer = new THREE.WebGLRenderer({
                canvas: document.getElementById('3d-canvas'),
                antialias: true
            });
            renderer.setSize(window.innerWidth, window.innerHeight);

            // Add light sources
            let ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
            scene.add(ambientLight);

            let sunLight = new THREE.PointLight(0xffffff, 1, 100);
            sunLight.position.set(0, 0, 0);
            scene.add(sunLight);

            // Add celestial bodies
            addCelestialBodies();

            // Add orbit paths
            addOrbitPaths();

            // Add event listeners
            document.getElementById('dwarf-planet-selector').addEventListener('click', selectDwarfPlanet);
            document.getElementById('time-slider').addEventListener('input', updateDateDisplay);
            document.getElementById('play-pause-button').addEventListener('click', toggleAnimation);
        }

        // Function to add celestial bodies
        function addCelestialBodies() {
            // Add sun
            let sunGeometry = new THREE.SphereGeometry(0.5, 60, 60);
            let sunMaterial = new THREE.MeshBasicMaterial({color: 0xffffff});
            let sun = new THREE.Mesh(sunGeometry, sunMaterial);
            scene.add(sun);

            // Add planets
            for (let planet in planets) {
                let geometry = new THREE.SphereGeometry(0.1, 60, 60);
                let material = new THREE.MeshLambertMaterial({color: getPlanetColor(planet)});
                let planetObject = new THREE.Mesh(geometry, material);
                scene.add(planetObject);

                let position = calculatePosition(new Date(2000, 0, 1, 12), planets[planet]);
                planetObject.position.copy(position);

                dwarfPlanetOrbitPaths[planet] = new THREE.EllipseCurve(
                    position.x,
                    position.y,
                    planets[planet].a,
                    0,
                    0,
                    Math.PI * 2,
                    false
                );
                let points = dwarfPlanetOrbitPaths[planet].getPoints(200);
                let geometry = new THREE.BufferGeometry().setFromPoints(points);
                let material = new THREE.LineBasicMaterial({color: getPlanetColor(planet)});
                let line = new THREE.Line(geometry, material);
                scene.add(line);
            }
        }

        // Function to add orbit paths
        function addOrbitPaths() {
            // Add planet orbits
            for (let planet in planets) {
                let position = calculatePosition(new Date(2000, 0, 1, 12), planets[planet]);
                let geometry = new THREE.EllipseCurve(
                    position.x,
                    position.y,
                    planets[planet].a,
                    0,
                    0,
                    Math.PI * 2,
                    false
                );
                let points = geometry.getPoints(200);
                let geometry = new THREE.BufferGeometry().setFromPoints(points);
                let material = new THREE.LineBasicMaterial({color: 0x0000ff});
                let line = new THREE.Line(geometry, material);
                scene.add(line);
            }
        }

        // Function to select dwarf planet
        function selectDwarfPlanet(event) {
            let selectedPlanet = event.target.dataset.planet;
            document.querySelector('#dwarf-planet-selector .active').classList.remove('active');
            event.target.classList.add('active');
            selectedDwarfPlanet = selectedPlanet;

            // Update the position of the selected dwarf planet
            let position = calculatePosition(new Date(2000, 0, 1, 12), planets[selectedPlanet]);
            let planetObject = scene.getObjectByName(selectedPlanet);
            planetObject.position.copy(position);

            // Update the orbit path of the selected dwarf planet
            let geometry = new THREE.EllipseCurve(
                position.x,
                position.y,
                planets[selectedPlanet].a,
                0,
                0,
                Math.PI * 2,
                false
            );
            let points = geometry.getPoints(200);
            let geometry = new THREE.BufferGeometry().setFromPoints(points);
            let material = new THREE.LineBasicMaterial({color: getPlanetColor(selectedPlanet)});
            let line = new THREE.Line(geometry, material);
            scene.add(line);
