        // Function to toggle animation
        function toggleAnimation() {
            if (isAnimating) {
                isAnimating = false;
                document.getElementById('play-pause-button').classList.remove('paused');
                document.getElementById('play-pause-button').classList.add('playing');
                document.getElementById('play-pause-button').textContent = '▶';
