            } else {
                isAnimating = true;
                document.getElementById('play-pause-button').classList.remove('playing');
                document.getElementById('play-pause-button').classList.add('paused');
                document.getElementById('play-pause-button').textContent = '⏸';
                cancelAnimationFrame(animate);
