        // Function to animate
        function animate() {
            let date = new Date(2000, 0, 1, 12);
            date.setDate(date.getDate() + parseInt(document.getElementById('time-slider').value));
            document.getElementById('date-display').innerHTML = formatDateTime(date);
            let value = parseInt(document.getElementById('time-slider').value);
            if (value >= 110000) {
                value = 0;
            }
            document.getElementById('time-slider').value = value + 2;
            requestAnimationFrame(animate);
        }

        // Function to get planet color
        function getPlanetColor(planet) {
            switch (planet) {
                case 'Mercury':
                    return 0x808080;
                case 'Venus':
                    return 0xbebebe;
                case 'Earth':
                    return 0x0000ff;
                case 'Mars':
                    return 0xff0000;
                case 'Ceres':
                    return 0x808080;
                case 'Pluto':
                    return 0x800080;
                case 'Haumea':
                    return 0x808080;
                case 'Makemake':
                    return 0x808080;
                case 'Eris':
                    return 0x800080;
                default:
                    return 0x808080;
            }
        }

        // Function to format date time
        function formatDateTime(date) {
            let day = date.getDate();
            let month = date.getMonth() + 1;
            let year = date.getFullYear();
            let hours = date.getHours();
            let minutes = date.getMinutes();
            let seconds = date.getSeconds();
            return day + ' ' + getMonthName(month) + ' ' + year + ' ' + hours + ':' + minutes + ':' + seconds;
        }

        // Function to get month name
        function getMonthName(month) {
            switch (month) {
                case 1:
                    return 'Jan';
                case 2:
                    return 'Feb';
                case 3:
                    return 'Mar';
                case 4:
                    return 'Apr';
                case 5:
                    return 'May';
                case 6:
                    return 'Jun';
                case 7:
                    return 'Jul';
                case 8:
                    return 'Aug';
                case 9:
                    return 'Sep';
                case 10:
                    return 'Oct';
                case 11:
                    return 'Nov';
                case 12:
                    return 'Dec';
                default:
                    return '';
            }
        }

        // Function to calculate position
        function calculatePosition(date, orbitalElements) {
            let d = (date.getTime() - new Date(2000, 0, 1, 12).getTime()) / (1000 * 3600 * 24);
            let M = (orbitalElements.M_epoch + 360 / (365.25 * Math.pow(orbitalElements.a, 1.5)) * d) % 360;
            let E = M - orbitalElements.e * Math.sin(M);
            while (E > Math.PI) {
                E -= 2 * Math.PI;
            }
            while (E < -Math.PI) {
                E += 2 * Math.PI;
            }
            let ν = 2 * Math.atan(Math.sqrt((1 + orbitalElements.e) / (1 - orbitalElements.e)) * Math.tan(E / 2));
            let r = orbitalElements.a * (1 - orbitalElements.e * Math.cos(E));
            let x_orb = r * Math.cos(ν);
            let y_orb = r * Math.sin(ν);
            let x = x_orb * Math.cos(orbitalElements.i * Math.PI / 180) * Math.cos(orbitalElements.Ω * Math.PI / 180) - y_orb * Math.sin(orbitalElements.i * Math.PI / 180);
            let y = x_orb * Math.cos(orbitalElements.i * Math.PI / 180) * Math.sin(orbitalElements.Ω * Math.PI / 180) + y_orb * Math.cos(orbitalElements.i * Math.PI / 180);
            let z = x_orb * Math.sin(orbitalElements.i * Math.PI / 180);
            return new THREE.Vector3(x, y, z);
        }

        // Initialization
        initScene();
    </script>
