            y: plotData['plot-cfc'].y.map((y, i) => currentCFC === selectCFC.value ? y[i] : null),
            type: 'scatter',
            mode: 'lines',
            line: { color: plotData['plot-cfc'].colors[plotData['plot-cfc'].y.findIndex(y => y.includes(currentCFC === selectCFC.value))], width: 2 },
            marker: { color: plotData['plot-cfc'].colors[plotData['plot-cfc'].y.findIndex(y => y.includes(currentCFC === selectCFC.value))], size: 10 },
            name: currentCFC
          }, {
            title: 'CFC concentration',
            xaxis: { title: 'year' },
            yaxis: { title: null }
          });

          const ozonePlot = plotly.newPlot(plotOzone, {
            x: plotData['plot-ozone'].x,
            y: plotData['plot-ozone'].y.map((y, i) => 1 / (y[i] + (currentTime / 10))),
            type: 'scatter',
            mode: 'lines',
            line: { color: 'blue', width: 2 },
            name: '1/[O3]'
          }, {
            title: 'concentration of ozone',
            xaxis: { title: 'time (s)' },
            yaxis: { title: '1/[O3]' }
          });

          const scene = new THREE.Scene();
          const camera = new THREE.PerspectiveCamera(75, sceneContainer.offsetWidth / sceneContainer.offsetHeight, 0.1, 1000);
          const renderer = new THREE.WebGLRenderer({
            canvas: document.createElement('canvas'),
            antialias: true
          });
          renderer.setSize(sceneContainer.offsetWidth, sceneContainer.offsetHeight);
          sceneContainer.appendChild(renderer.domElement);

          const earth = new THREE.Mesh(new THREE.SphereGeometry(1, 32, 32), new THREE.MeshBasicMaterial({ color: 0x0000ff }));
          scene.add(earth);

          const ozoneLayer = new THREE.Mesh(new THREE.SphereGeometry(1.1, 32, 32), new THREE.MeshBasicMaterial({ color: 0x00ff00, transparent: true }));
          ozoneLayer.material.opacity = 0.5;
          scene.add(ozoneLayer);

          const atmosphere = new THREE.Mesh(new THREE.SphereGeometry(2, 32, 32), new THREE.MeshBasicMaterial({ color: 0xffaa88, transparent: true }));
          atmosphere.material.opacity = 0.1;
          scene.add(atmosphere);

          const sun = new THREE.Mesh(new THREE.SphereGeometry(0.1, 32, 32), new THREE.MeshBasicMaterial({ color: 0xffff00 }));
          scene.add(sun);

          const radiation = new THREE.Group();
          for (let i = 0; i < 5; i++) {
            const arrow = new THREE.ArrowHelper(new THREE.Vector3(1, 0, 0), new THREE.Vector3(-1, 0, 0), 1, 0xffaa88, 1);
            radiation.add(arrow);
          }
          scene.add(radiation);

          camera.position.z = 5;

          function animate() {
            requestAnimationFrame(animate);
            ozoneLayer.material.opacity = 0.5 - (cfcData[currentCFC].data.find(([x, y]) => x === currentYear).y / 500);
            renderer.render(scene, camera);
          }
          animate();
        }

        update();
    </script>
</body>
</html>
