        <canvas id="demo-canvas"></canvas>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let angle = 7;
        let isZoomed = false;

        function setup() {
            createCanvas(600, 450);
            background(220);
            angleMode(DEGREES);
        }

        function draw() {
            background(220);
            if (!isZoomed) {
                drawFullView();
            } else {
                drawZoomedView();
            }
        }

        function drawFullView() {
            // Earth
            fill(114, 138, 150);
            ellipse(width / 2, height / 2, 300);
            fill(0);
            textSize(24);
            textAlign(CENTER, CENTER);
            text("Earth", width / 2, height / 2);

            // Syene
            stroke(0);
            line(width / 2, height / 2, width, height / 2);
            fill(0);
            textSize(18);
            textAlign(LEFT, CENTER);
            text("Syene", width, height / 2);

            // Alexandria
            let alexandriaX = width / 2 + cos(angle) * 200;
            let alexandriaY = height / 2 + sin(angle) * 200;
            stroke(0);
            line(width / 2, height / 2, alexandriaX, alexandriaY);
            line(alexandriaX, alexandriaY, width / 2 + cos(angle) * 200, height / 2);
            fill(0);
            textSize(18);
            textAlign(LEFT, CENTER);
            text("Alexandria", alexandriaX, alexandriaY);

            // Angle visualization
            stroke(0);
            let angleStartX = width / 2 + cos(angle) * 200;
            let angleStartY = height / 2 + sin(angle) * 200;
            let angleEndX = width / 2 + cos(angle) * 100;
            let angleEndY = height / 2 + sin(angle) * 100;
            arc(angleStartX, angleStartY, 50, 50, 0, angle);
            fill(0);
            textSize(18);
            textAlign(LEFT, CENTER);
            text(angle.toFixed(1) + "°", angleStartX, angleStartY);
            triangle(width / 2 + cos(angle) * 195, height / 2 + sin(angle) * 195, width / 2 + cos(angle) * 205, height / 2 + sin(angle) * 205, width / 2 + cos(angle) * 200, height / 2 + sin(angle) * 200);
        }

        function drawZoomedView() {
            // Earth sector
            fill(114, 138, 150);
            arc(width / 2, height / 2, 300, 150, 0, angle);

            // Radii
            stroke(0);
            line(width / 2, height / 2, width, height / 2);
            line(width / 2, height / 2, width / 2 + cos(angle) * 100, height / 2 + sin(angle) * 100);

            // Labels
            fill(0);
            textSize(18);
            textAlign(LEFT, CENTER);
            text("Syene", width, height / 2);
            text("Alexandria", width / 2 + cos(angle) * 100, height / 2 + sin(angle) * 100);

            // Angle visualization
            stroke(0);
            let angleStartX = width / 2 + cos(angle) * 100;
            let angleStartY = height / 2 + sin(angle) * 100;
            let angleEndX = width / 2 + cos(angle) * 50;
            let angleEndY = height / 2 + sin(angle) * 50;
            arc(angleStartX, angleStartY, 50, 50, 0, angle);
            fill(0);
            textSize(18);
            textAlign(LEFT, CENTER);
            text(angle.toFixed(1) + "°", angleStartX, angleStartY);

            // Post and shadow
            stroke(0);
            fill(128, 64, 0);
            triangle(width / 2 + cos(angle) * 95, height / 2 + sin(angle) * 95, width / 2 + cos(angle) * 105, height / 2 + sin(angle) * 105, width / 2 + cos(angle) * 100, height / 2 + sin(angle) * 100);
        }

        function updateDisplayPostAngle() {
            let displayPostAngle = document.getElementById("display-post-angle");
            displayPostAngle.textContent = angle.toFixed(1) + "°";
        }

        function sliderPostAngleChanged() {
            angle = Number(document.getElementById("slider-post-angle").value);
            updateDisplayPostAngle();
            redraw();
        }

        function checkboxZoomChanged() {
            isZoomed = document.getElementById("checkbox-zoom").checked;
            redraw();
        }

        document.getElementById("slider-post-angle").addEventListener("input", sliderPostAngleChanged);
        document.getElementById("checkbox-zoom").addEventListener("change", checkboxZoomChanged);

        updateDisplayPostAngle();
        setup();
        draw();
    </script>
</body>
</html>
