<body>
    <div id="app-container">
        <div id="control-panel">
            <button id="btn-reset">+</button>
            <div id="slider-container">
                <label for="slider-moon-pos">position of moon</label>
                <input type="range" id="slider-moon-pos" min="0" max="360" value="144" step="1">
                <label for="slider-moon-pull">pull from moon</label>
                <input type="range" id="slider-moon-pull" min="0" max="100" value="40" step="1">
                <label for="slider-sun-pos">position of sun</label>
                <input type="range" id="slider-sun-pos" min="0" max="360" value="144" step="1">
                <label for="slider-sun-pull">pull from sun</label>
                <input type="range" id="slider-sun-pull" min="0" max="100" value="20" step="1">
            </div>
        </div>
        <div id="viz-container"></div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/dist/p5.js"></script>
    <script>
        let moonAngle = 144;
        let moonPull = 40;
        let sunAngle = 144;
        let sunPull = 20;
        let baseWaterRadius = 200;
        let scalingFactor = 0.5;

        function setup() {
            createCanvas(500, 500);
            angleMode(DEGREES);
            noLoop();
        }

        function draw() {
            background(220);
            let moonAngle_rad = radians(moonAngle);
            let sunAngle_rad = radians(sunAngle);
            let waterPoints = [];

            for (let theta = 0; theta < 360; theta++) {
                let totalRadius = baseWaterRadius + scalingFactor * (moonPull * Math.pow(Math.cos(theta - moonAngle_rad), 2) + sunPull * Math.pow(Math.cos(theta - sunAngle_rad), 2));
                let x = totalRadius * Math.cos(radians(theta));
                let y = totalRadius * Math.sin(radians(theta));
                waterPoints.push(x, y);
            }

            beginShape();
            noStroke();
            fill(135, 218, 241);
            vertex(waterPoints[0]);
            for (let i = 1; i < waterPoints.length; i++) {
                vertex(waterPoints[i]);
            }
            endShape(CLOSE);

            fill(150, 111, 51);
            ellipse(width / 2, height / 2, 360);
        }

        function redraw() {
            moonAngle = document.getElementById('slider-moon-pos').value;
            moonPull = document.getElementById('slider-moon-pull').value;
            sunAngle = document.getElementById('slider-sun-pos').value;
            sunPull = document.getElementById('slider-sun-pull').value;
            draw();
        }

        document.getElementById('slider-moon-pos').addEventListener('input', redraw);
        document.getElementById('slider-moon-pull').addEventListener('input', redraw);
        document.getElementById('slider-sun-pos').addEventListener('input', redraw);
        document.getElementById('slider-sun-pull').addEventListener('input', redraw);

        document.getElementById('btn-reset').addEventListener('click', function() {
            document.getElementById('slider-moon-pos').value = 144;
            document.getElementById('slider-moon-pull').value = 40;
            document.getElementById('slider-sun-pos').value = 144;
            document.getElementById('slider-sun-pull').value = 20;
            redraw();
        });

        setup();
        redraw();
    </script>
</body>
</html>
