    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script>
        // Get the canvas elements
        const telescopicCanvas = document.getElementById('telescopic-canvas');
        const orbitCanvas = document.getElementById('orbit-canvas');

        // Create the Three.js scenes and renderers
        const telescopicScene = new THREE.Scene();
        const orbitScene = new THREE.Scene();

        const telescopicRenderer = new THREE.WebGLRenderer({
            canvas: telescopicCanvas,
            antialias: true
        });
        const orbitRenderer = new THREE.WebGLRenderer({
            canvas: orbitCanvas,
            antialias: true
        });

        // Set up the camera and lighting for the telescopic view
        const telescopicCamera = new THREE.PerspectiveCamera(15, 1, 1, 1000);
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        telescopicScene.add(ambientLight);

        // Set up the camera and lighting for the orbit view
        const orbitCamera = new THREE.PerspectiveCamera(30, 1, 1, 1000);
        const sunLight = new THREE.PointLight(0xffffff, 1, 1000);
        sunLight.position.set(0, 0, 0);
        orbitScene.add(sunLight);
        const ambientLightOrbit = new THREE.AmbientLight(0xffffff, 0.5);
        orbitScene.add(ambientLightOrbit);

        // Create the celestial bodies
        const sun = new THREE.Mesh(new THREE.SphereGeometry(0.1, 10, 10), new THREE.MeshBasicMaterial({ color: 0xffffff }));
        sun.position.set(0, 0, 0);
        orbitScene.add(sun);

        const earth = new THREE.Mesh(new THREE.SphereGeometry(0.2, 10, 10), new THREE.MeshBasicMaterial({ color: 0x0000ff }));
        earth.position.set(4 * Math.cos(Math.PI * 180 / 360 * 180), 0, 4 * Math.sin(Math.PI * 180 / 360 * 180));
        orbitScene.add(earth);

        const cometNucleus = new THREE.Mesh(new THREE.SphereGeometry(0.1, 10, 10), new THREE.MeshBasicMaterial({ color: 0xffffff, emissive: 0xffffff }));
        cometNucleus.position.set(14.4 * Math.cos(Math.PI * 180 / 360 * 180), 0, 14.4 * Math.sin(Math.PI * 180 / 360 * 180));
        orbitScene.add(cometNucleus);

        const cometComa = new THREE.Mesh(new THREE.SphereGeometry(1, 10, 10), new THREE.MeshBasicMaterial({ color: 0xffff00, opacity: 0.5, transparent: true }));
        cometComa.position.copy(cometNucleus.position);
        cometComa.scale.set(0.01, 0.01, 0.01);
        orbitScene.add(cometComa);

        const cometTail = new THREE.Mesh(new THREE.ConeGeometry(0.5, 10, 10), new THREE.MeshBasicMaterial({ color: 0xffff00, opacity: 0.4, transparent: true }));
        cometTail.position.copy(cometNucleus.position);
        cometTail.scale.set(0.01, 0.01, 0.01);
        cometTail.rotation.x = Math.PI / 2;
        orbitScene.add(cometTail);

        // Create the comet model for the telescopic view
        const cometModel = new THREE.Group();
        cometModel.add(cometNucleus.clone());
        cometModel.add(cometComa.clone());
        cometModel.add(cometTail.clone());
        telescopicScene.add(cometModel);

        // Set up the event listeners for the sliders
        document.getElementById('slider-earth-position').addEventListener('input', () => {
            earth.position.set(4 * Math.cos(Math.PI * 180 / 360 * parseFloat(document.getElementById('slider-earth-position').value)), 0, 4 * Math.sin(Math.PI * 180 / 360 * parseFloat(document.getElementById('slider-earth-position').value)));
            const target = cometNucleus.position;
            telescopicCamera.position.set(earth.position.x, earth.position.y + 10, earth.position.z);
            telescopicCamera.lookAt(target);
        });
        document.getElementById('slider-comet-position').addEventListener('input', () => {
            cometNucleus.position.set(14.4 * Math.cos(Math.PI * 180 / 360 * parseFloat(document.getElementById('slider-comet-position').value)), 0, 14.4 * Math.sin(Math.PI * 180 / 360 * parseFloat(document.getElementById('slider-comet-position').value)));
            cometComa.position.copy(cometNucleus.position);
            cometTail.position.copy(cometNucleus.position);
            cometTail.rotation.x = Math.PI / 2;
        });
        document.getElementById('slider-coma').addEventListener('input', () => {
            cometComa.scale.set(parseFloat(document.getElementById('slider-coma').value) / 100, parseFloat(document.getElementById('slider-coma').value) / 100, parseFloat(document.getElementById('slider-coma').value) / 100);
        });
        document.getElementById('slider-tail').addEventListener('input', () => {
            cometTail.scale.set(parseFloat(document.getElementById('slider-tail').value) / 100, parseFloat(document.getElementById('slider-tail').value) / 100, parseFloat(document.getElementById('slider-tail').value) / 100);
        });
        document.getElementById('slider-tail-spread').addEventListener('input', () => {
            cometTail.scale.set(parseFloat(document.getElementById('slider-tail-spread').value) / 100, parseFloat(document.getElementById('slider-tail-spread').value) / 100, parseFloat(document.getElementById('slider-tail-spread').value) / 100);
        });

        // Set up the animation loop
        function animate() {
            requestAnimationFrame(animate);
            orbitRenderer.render(orbitScene, orbitCamera);
            telescopicRenderer.render(telescopicScene, telescopicCamera);
        }
        animate();
    </script>
</body>
</html>
