            let step = 10;
            for (let i = 100; i <= 700; i += step) {
                line(i, 250, i, 250 + 200);
            }
        }

        function drawAngles() {
            let theta1 = atan(y / x);
            let theta2 = atan(y / (L - x));
            arc(100, 250 + (y / (L - x)) * 200, 20, 20, 0, theta1);
            arc(700, 250 + (y / x) * 200, 20, 20, 0, theta2);
        }

        function drawLabels() {
            fill(0);
            text("T₁", 150, 220);
            text("T₂", 650, 220);
            text("θ₁", 150, 220 + 20);
            text("θ₂", 650, 220 + 20);
        }

        function drawTensions() {
            let tension1 = (m * g * cos(theta2)) / sin(theta1 + theta2);
            let tension2 = (m * g * cos(theta1)) / sin(theta1 + theta2);
            fill(0);
            text(`T₁ = ${tension1.toFixed(4)} N | T₂ = ${tension2.toFixed(4)} N`, width / 2, 20);
        }

        function mouseDragged() {
            m = document.getElementById("slider-m").value;
            x = document.getElementById("slider-x").value;
            y = document.getElementById("slider-y").value;
            draw();
        }

        setup();
        document.getElementById("slider-m").addEventListener("input", mouseDragged);
        document.getElementById("slider-x").addEventListener("input", mouseDragged);
        document.getElementById("slider-y").addEventListener("input", mouseDragged);
    </script>
</body>
</html>
