    <script>
        let time = 0;
        let animationFrameId = null;
        let isRunning = false;

        const controls = document.getElementById('controls-container');
        const sceneContainer = document.getElementById('scene-container');
        const plotDisplacement = document.getElementById('plot-displacement');
        const plotVelocity = document.getElementById('plot-velocity');

        const sliders = {
            displacement: document.getElementById('slider-displacement'),
            velocity: document.getElementById('slider-velocity'),
            stiffness: document.getElementById('slider-stiffness'),
            mass: document.getElementById('slider-mass'),
            damping: document.getElementById('slider-damping'),
            proportionalGain: document.getElementById('slider-proportional-gain'),
            derivativeGain: document.getElementById('slider-derivative-gain'),
        };

        const values = {
            displacement: document.getElementById('value-displacement'),
            velocity: document.getElementById('value-velocity'),
            stiffness: document.getElementById('value-stiffness'),
            mass: document.getElementById('value-mass'),
            damping: document.getElementById('value-damping'),
            proportionalGain: document.getElementById('value-proportional-gain'),
            derivativeGain: document.getElementById('value-derivative-gain'),
        };

        const buttons = {
            play: document.getElementById('btn-play'),
            pause: document.getElementById('btn-pause'),
            reset: document.getElementById('btn-reset'),
        };

        let scene, camera, renderer, mass, spring, damper, enclosure;

        function initThree() {
            scene = new THREE.Scene();
            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            renderer = new THREE.WebGLRenderer({
                canvas: document.createElement('canvas'),
                antialias: true,
            });
            renderer.setSize(window.innerWidth, window.innerHeight);
            sceneContainer.appendChild(renderer.domElement);

            mass = new THREE.Mesh(new THREE.BoxGeometry(1, 1, 1), new THREE.MeshStandardMaterial({ color: 0xFFFF00 }));
            spring = new THREE.Mesh(new THREE.TubeGeometry(0.1, 100, 5, 10, Math.PI * 2, false), new THREE.MeshStandardMaterial({ color: 0xFF0000 }));
            damper = new THREE.Group();
            damper.add(new THREE.Mesh(new THREE.CylinderGeometry(0.1, 0.1, 2, 32), new THREE.MeshStandardMaterial({ color: 0x0000FF })));
            damper.add(new THREE.Mesh(new THREE.CylinderGeometry(0.1, 0.1, 2, 32), new THREE.MeshStandardMaterial({ color: 0x0000FF })));
            enclosure = new THREE.Mesh(new THREE.BoxGeometry(10, 10, 10), new THREE.MeshBasicMaterial({ color: 0x00FF00, wireframe: true }));

            scene.add(mass);
            scene.add(spring);
            scene.add(damper);
            scene.add(enclosure);

            camera.position.z = 10;
            camera.position.y = 5;
        }

        function animate() {
            requestAnimationFrame(animate);
            if (isRunning) {
                time += 0.01;
                mass.position.x = Math.sin(time) * 2;
                spring.scale.x = 1 + Math.sin(time) * 0.5;
                damper.position.x = mass.position.x + 1;
                renderer.render(scene, camera);
            }
        }

        function drawPlot(plotId, title, x, y, solutionCurve, currentTimeMarker) {
            const plot = plotly.newPlot(plotId, [
                {
                    x: x,
                    y: y,
                    mode: 'lines',
                    name: title,
                },
                {
                    x: [currentTimeMarker.x],
                    y: [currentTimeMarker.y],
                    mode: 'markers',
                    marker: {
                        color: title === 'Displacement' ? 'blue' : 'green',
                    },
                },
            ], {
                title: title,
                xaxis: {
                    title: 'Time (s)',
                    range: [0, 10],
                },
                yaxis: {
                    title: title === 'Displacement' ? 'Displacement (m)' : 'Velocity (m/s)',
                    range: [-3, 3],
                },
            });
        }

        function updatePlots() {
            const x = Array(101).fill(0).map((_, i) => i / 10);
            const yDisplacement = Array(101).fill(0).map((_, i) => Math.sin(x[i]) * 2);
            const yVelocity = Array(101).fill(0).map((_, i) => Math.cos(x[i]) * 2);

            drawPlot('plot-displacement', 'Displacement', x, yDisplacement, { x: x, y: yDisplacement }, { x: time, y: Math.sin(time) * 2 });
            drawPlot('plot-velocity', 'Velocity', x, yVelocity, { x: x, y: yVelocity }, { x: time, y: Math.cos(time) * 2 });
        }

        function updateScene() {
            mass.position.x = Math.sin(time) * 2;
            spring.scale.x = 1 + Math.sin(time) * 0.5;
            damper.position.x = mass.position.x + 1;
            renderer.render(scene, camera);
        }

        function updateValues() {
            values.displacement.textContent = Math.round(sliders.displacement.value * 10) / 10;
            values.velocity.textContent = Math.round(sliders.velocity.value * 10) / 10;
            values.stiffness.textContent = Math.round(sliders.stiffness.value * 100) / 100;
            values.mass.textContent = Math.round(sliders.mass.value * 100) / 100;
            values.damping.textContent = Math.round(sliders.damping.value * 10) / 10;
            values.proportionalGain.textContent = Math.round(sliders.proportionalGain.value * 10) / 10;
            values.derivativeGain.textContent = Math.round(sliders.derivativeGain.value * 10) / 10;
        }

        function handleSliderChange() {
            updateValues();
            time = 0;
            isRunning = false;
            animationFrameId = null;
            sceneContainer.innerHTML = '';
            initThree();
            updateScene();
            updatePlots();
        }

        function handleButtonPress(button) {
            if (button.id === 'btn-play') {
                isRunning = true;
                animationFrameId = requestAnimationFrame(animate);
            } else if (button.id === 'btn-pause') {
                isRunning = false;
            } else if (button.id === 'btn-reset') {
                time = 0;
                mass.position.x = 0;
                spring.scale.x = 1;
                damper.position.x = mass.position.x + 1;
                updateScene();
                updatePlots();
            }
        }

        sliders.displacement.addEventListener('input', handleSliderChange);
        sliders.velocity.addEventListener('input', handleSliderChange);
        sliders.stiffness.addEventListener('input', handleSliderChange);
        sliders.mass.addEventListener('input', handleSliderChange);
        sliders.damping.addEventListener('input', handleSliderChange);
        sliders.proportionalGain.addEventListener('input', handleSliderChange);
        sliders.derivativeGain.addEventListener('input', handleSliderChange);

        buttons.play.addEventListener('click', () => handleButtonPress(buttons.play));
        buttons.pause.addEventListener('click', () => handleButtonPress(buttons.pause));
        buttons.reset.addEventListener('click', () => handleButtonPress(buttons.reset));

        initThree();
        updateScene();
        updatePlots();
    </script>
</body>
</html>
