    <script>
        let isAnimating = false;
        let t = 0;
        let tensionValue = 50;
        let x1Value = 0.1;
        let x2Value = 0.1;
        let x3Value = 0.0;
        let m1Value = 1.0;
        let m2Value = 1.0;
        let m3Value = 1.0;

        function setup() {
            createCanvas(800, 600);
            background(220);
            stroke(0);
            strokeWeight(2);
            noLoop();
        }

        function draw() {
            background(220);
            stroke(0);
            strokeWeight(2);
            line(0, 0, 1, x1Value);
            line(1, x1Value, 2, x2Value);
            line(2, x2Value, 3, x3Value);
            line(3, x3Value, 4, 0);
            fill(255, 0, 0);
            for (let i = 0; i < 3; i++) {
                ellipse(1 + i, x1Value, 10 * sqrt(m1Value), 10 * sqrt(m1Value));
            }
        }

        function update() {
            if (isAnimating) {
                t += 0.01;
                x1Value = 0.1 * cos(t);
                x2Value = 0.1 * cos(t + 2 * PI / 3);
                x3Value = 0.0 * cos(t + 4 * PI / 3);
                draw();
            }
        }

        function animationLoop() {
            if (isAnimating) {
                update();
                requestAnimationFrame(animationLoop);
            }
        }

        function toggleAnimation() {
            isAnimating = !isAnimating;
            if (isAnimating) {
                animationLoop();
                document.getElementById('btn-play-pause').textContent = '❚❚';
            } else {
                noLoop();
                document.getElementById('btn-play-pause').textContent = '▶';
            }
        }

        function reset() {
            isAnimating = false;
            noLoop();
            document.getElementById('btn-play-pause').textContent = '▶';
            t = 0;
            x1Value = 0.1;
            x2Value = 0.1;
            x3Value = 0.0;
            draw();
        }

        function updateValues() {
            tensionValue = document.getElementById('slider-tension').value;
            x1Value = document.getElementById('slider-x1').value;
            x2Value = document.getElementById('slider-x2').value;
            x3Value = document.getElementById('slider-x3').value;
            m1Value = document.getElementById('slider-m1').value;
            m2Value = document.getElementById('slider-m2').value;
            m3Value = document.getElementById('slider-m3').value;
            document.getElementById('tension-value').textContent = tensionValue;
            reset();
        }

        document.getElementById('slider-tension').addEventListener('input', updateValues);
        document.getElementById('slider-x1').addEventListener('input', updateValues);
        document.getElementById('slider-x2').addEventListener('input', updateValues);
        document.getElementById('slider-x3').addEventListener('input', updateValues);
        document.getElementById('slider-m1').addEventListener('input', updateValues);
        document.getElementById('slider-m2').addEventListener('input', updateValues);
        document.getElementById('slider-m3').addEventListener('input', updateValues);
        document.getElementById('btn-play-pause').addEventListener('click', toggleAnimation);
        document.getElementById('btn-reset').addEventListener('click', reset);

        setup();
        draw();
    </script>
</body>
