    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let mode = 'move';
        let points = [
            {x: 150, y: 350},
            {x: 250, y: 150},
            {x: 350, y: 250},
            {x: 550, y: 180},
            {x: 480, y: 320},
            {x: 420, y: 400}
        ];
        let draggedPoint = null;
        let algorithmState = {
            step: 0,
            hullPointsIndices: [],
            fullHull: [],
            leftmostPointIndex: null,
            currentPointIndex: null,
            candidateLines: [],
            explanationSteps: []
        };

        function setup() {
            createCanvas(700, 500);
            background(255);
            noStroke();
            fill(200, 220, 100);
            for (let i = 0; i < points.length; i++) {
                ellipse(points[i].x, points[i].y, 12);
                line(points[i].x - 6, points[i].y, points[i].x + 6, points[i].y);
                line(points[i].x, points[i].y - 6, points[i].x, points[i].y + 6);
            }
        }

        function draw() {
            background(255);
            if (mode === 'move') {
                for (let i = 0; i < points.length; i++) {
                    if (i === draggedPoint) {
                        fill(255, 0, 0);
                    } else {
                        fill(200, 220, 100);
                    }
                    ellipse(points[i].x, points[i].y, 12);
                    line(points[i].x - 6, points[i].y, points[i].x + 6, points[i].y);
                    line(points[i].x, points[i].y - 6, points[i].x, points[i].y + 6);
                }
            } else if (mode === 'algorithm') {
                for (let i = 0; i < points.length; i++) {
                    if (algorithmState.hullPointsIndices.includes(i)) {
                        fill(45, 60, 110);
                    } else {
                        fill(200, 220, 100);
                    }
                    ellipse(points[i].x, points[i].y, 12);
                    line(points[i].x - 6, points[i].y, points[i].x + 6, points[i].y);
                    line(points[i].x, points[i].y - 6, points[i].x, points[i].y + 6);
                }
                for (let i = 0; i < algorithmState.candidateLines.length; i++) {
                    let fromIndex = algorithmState.candidateLines[i][0];
                    let toIndex = algorithmState.candidateLines[i][1];
                    stroke(60, 150, 140);
                    strokeWeight(2);
                    line(points[fromIndex].x, points[fromIndex].y, points[toIndex].x, points[toIndex].y);
                }
                for (let i = 0; i < algorithmState.hullPointsIndices.length - 1; i++) {
                    let fromIndex = algorithmState.hullPointsIndices[i];
                    let toIndex = algorithmState.hullPointsIndices[i + 1];
                    stroke(45, 60, 110);
                    strokeWeight(3);
                    line(points[fromIndex].x, points[fromIndex].y, points[toIndex].x, points[toIndex].y);
                }
            }
        }

        function mousePressed() {
            if (mode === 'move') {
                for (let i = 0; i < points.length; i++) {
                    let distance = dist(mouseX, mouseY, points[i].x, points[i].y);
                    if (distance < 6) {
                        draggedPoint = i;
                    }
                }
            }
        }

        function mouseDragged() {
            if (mode === 'move' && draggedPoint !== null) {
                points[draggedPoint].x = mouseX;
                points[draggedPoint].y = mouseY;
                redraw();
            }
        }

        function mouseReleased() {
            if (mode === 'move') {
                draggedPoint = null;
            }
        }

        function btnMoveClick() {
            mode = 'move';
            algorithmState.step = 0;
            algorithmState.hullPointsIndices = [];
            algorithmState.leftmostPointIndex = null;
            algorithmState.currentPointIndex = null;
            algorithmState.candidateLines = [];
            algorithmState.explanationSteps = [];
            document.getElementById('step-controls').style.display = 'none';
            document.getElementById('explanation-text').innerHTML = '';
            redraw();
        }

        function btnStartAlgoClick() {
            mode = 'algorithm';
            document.getElementById('btn-move').disabled = true;
            document.getElementById('step-controls').style.display = 'block';
            document.getElementById('step1').disabled = false;
            document.getElementById('btn-step1-explain').disabled = false;
            document.getElementById('btn-step1-do').disabled = false;
            document.getElementById('step2').disabled = true;
            document.getElementById('btn-step2-explain').disabled = true;
            document.getElementById('btn-step2-do').disabled = true;
            document.getElementById('step3').disabled = true;
            document.getElementById('btn-step3-explain').disabled = true;
            document.getElementById('step3-actions').innerHTML = '';
            algorithmState.fullHull = [];
            algorithmState.leftmostPointIndex = null;
            algorithmState.currentPointIndex = null;
            algorithmState.candidateLines = [];
            algorithmState.explanationSteps = [];
            for (let i = 0; i < points.length; i++) {
                algorithmState.fullHull.push(i);
            }
            for (let i = 0; i < algorithmState.fullHull.length - 2; i++) {
                let button = document.createElement('button');
                button.id = 'btn-step3-repeat-' + i;
                button.textContent = 'repeat';
                document.getElementById('step3-actions').appendChild(button);
            }
            document.getElementById('btn-step3-repeat-0').disabled = false;
            document.getElementById('btn-step3-do').disabled = false;
            redraw();
        }

        function btnStep1ExplainClick() {
            algorithmState.explanationSteps.push('Step 1. Find the leftmost point.');
            document.getElementById('explanation-text').innerHTML = algorithmState.explanationSteps.join('<br>');
        }

        function btnStep1DoClick() {
            let leftmostPointIndex = null;
            let min_x = Infinity;
            for (let i = 0; i < points.length; i++) {
                if (points[i].x < min_x) {
                    min_x = points[i].x;
                    leftmostPointIndex = i;
                }
            }
            algorithmState.leftmostPointIndex = leftmostPointIndex;
            algorithmState.currentPointIndex = leftmostPointIndex;
            algorithmState.hullPointsIndices.push(leftmostPointIndex);
            document.getElementById('step1').disabled = true;
            document.getElementById('btn-step1-explain').disabled = true;
            document.getElementById('btn-step1-do').disabled = true;
            document.getElementById('step2').disabled = false;
            document.getElementById('btn-step2-explain').disabled = false;
            document.getElementById('btn-step2-do').disabled = false;
            redraw();
        }

        function btnStep2ExplainClick() {
            algorithmState.explanationSteps.push('Step 2. Find the point clockwise furthest to the right relative to the leftmost point.');
            document.getElementById('explanation-text').innerHTML = algorithmState.explanationSteps.join('<br>');
        }

        function btnStep2DoClick() {
            algorithmState.candidateLines = [];
            for (let i = 0; i < points.length; i++) {
                if (!algorithmState.hullPointsIndices.includes(i)) {
                    for (let j = 0; j < points.length; j++) {
                        if (i !== j && !algorithmState.hullPointsIndices.includes(j)) {
                            algorithmState.candidateLines.push([i, j]);
                        }
                    }
                }
            }
            let secondPointIndex = algorithmState.fullHull[1];
            algorithmState.currentPointIndex = secondPointIndex;
            algorithmState.hullPointsIndices.push(secondPointIndex);
            document.getElementById('step2').disabled = true;
            document.getElementById('btn-step2-explain').disabled = true;
            document.getElementById('btn-step2-do').disabled = true;
            document.getElementById('step3').disabled = false;
            document.getElementById('btn-step3-explain').disabled = false;
            document.getElementById('btn-step3-do').disabled = false;
            redraw();
        }

        function btnStep3ExplainClick() {
            algorithmState.explanationSteps.push('Step 3. Repeat finding the point clockwise furthest to the right relative to the previously found point, until the leftmost point is reached again.');
            document.getElementById('explanation-text').innerHTML = algorithmState.explanationSteps.join('<br>');
        }

        function btnStep3DoClick() {
            let nextPointIndex = algorithmState.fullHull[algorithmState.currentPointIndex + 1];
            algorithmState.candidateLines = [];
            for (let i = 0; i < points.length; i++) {
                if (!algorithmState.hullPointsIndices.includes(i)) {
                    for (let j = 0; j < points.length; j++) {
                        if (i !== j && !algorithmState.hullPointsIndices.includes(j)) {
                            algorithmState.candidateLines.push([i, j]);
                        }
                    }
                }
            }
            let canvas = document.getElementById('canvas-container');
            let ctx = canvas.getContext('2d');
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            for (let i = 0; i < points.length; i++) {
                if (algorithmState.hullPointsIndices.includes(i)) {
                    fill(45, 60, 110);
                } else {
                    fill(200, 220, 100);
                }
                ellipse(points[i].x, points[i].y, 12);
                line(points[i].x - 6, points[i].y, points[i].x + 6, points[i].y);
                line(points[i].x, points[i].y - 6, points[i].x, points[i].y + 6);
            }
            for (let i = 0; i < algorithmState.candidateLines.length; i++) {
                let fromIndex = algorithmState.candidateLines[i][0];
                let toIndex = algorithmState.candidateLines[i][1];
                stroke(60, 150, 140);
                strokeWeight(2);
                line(points[fromIndex].x, points[fromIndex].y, points[toIndex].x, points[toIndex].y);
            }
            for (let i = 0; i < algorithmState.hullPointsIndices.length - 1; i++) {
                let fromIndex = algorithmState.hullPointsIndices[i];
                let toIndex = algorithmState.hullPointsIndices[i + 1];
                stroke(45, 60, 110);
                strokeWeight(3);
                line(points[fromIndex].x, points[fromIndex].y, points[toIndex].x, points[toIndex].y);
            }
            stroke(45, 60, 110);
            strokeWeight(3);
            line(points[algorithmState.currentPointIndex].x, points[algorithmState.currentPointIndex].y, points[nextPointIndex].x, points[nextPointIndex].y);
            fill(45, 60, 110);
            ellipse(points[nextPointIndex].x, points[nextPointIndex].y, 12);
            line(points[nextPointIndex].x - 6, points[nextPointIndex].y, points[nextPointIndex].x + 6, points[nextPointIndex].y);
            line(points[nextPointIndex].x, points[nextPointIndex].y - 6, points[nextPointIndex].x, points[nextPointIndex].y + 6);
            algorithmState.currentPointIndex = nextPointIndex;
            algorithmState.hullPointsIndices.push(nextPointIndex);
            document.getElementById('btn-step3-' + algorithmState.currentPointIndex).disabled = true;
            if (algorithmState.currentPointIndex === algorithmState.leftmostPointIndex) {
                for (let i = 0; i < algorithmState.hullPointsIndices.length - 1; i++) {
                    let fromIndex = algorithmState.hullPointsIndices[i];
                    let toIndex = algorithmState.hullPointsIndices[i + 1];
                    stroke(45, 60, 110);
                    strokeWeight(3);
                    line(points[fromIndex].x, points[fromIndex].y, points[toIndex].x, points[toIndex].y);
                }
                algorithmState.currentPointIndex = null;
                algorithmState.leftmostPointIndex = null;
                algorithmState.hullPointsIndices = [];
                document.getElementById('step3').disabled = true;
                document.getElementById('btn-step3-explain').disabled = true;
                document.getElementById('btn-step3-do').disabled = true;
            }
            redraw();
        }

        document.getElementById('btn-move').addEventListener('click', btnMoveClick);
        document.getElementById('btn-start-algo').addEventListener('click', btnStartAlgoClick);
        document.getElementById('btn-step1-explain').addEventListener('click', btnStep1ExplainClick);
        document.getElementById('btn-step1-do').addEventListener('click', btnStep1DoClick);
        document.getElementById('btn-step2-explain').addEventListener('click', btnStep2ExplainClick);
        document.getElementById('btn-step2-do').addEventListener('click', btnStep2DoClick);
        document.getElementById('btn-step3-explain').addEventListener('click', btnStep3ExplainClick);
        document.getElementById('btn-step3-do').addEventListener('click', btnStep3DoClick);
        for (let i = 0; i < algorithmState.fullHull.length - 2; i++) {
            document.getElementById('btn-step3-repeat-' + i).addEventListener('click', btnStep3DoClick);
        }

        setup();
    </script>
</body>
</html>
