        // Define initial conditions
        let X0 = 0.1;
        let Glu0 = 60;
        let Eta0 = 0;
        let E01 = 1.0;
        let E11 = 0.0;
        let E21 = 0.0;
        let V0 = 1.0;

        // Define slider values
        let alphaMax = 0.3;
        let tMin = 0;
        let tMax = 60;
        let enzymeControl = 1;
        let feedRate = 0.2;
        let substrateFeedConc = 100;
        let feedTime = 35;

        // Define plot data
        let plotTopData = {
            x: [],
            y: [],
            name: 'X(t)',
            type: 'line',
            line: { color: 'blue' }
        };
        let plotBottomData = {
            x: [],
            y: [],
            name: 'E₂(t)',
            type: 'line',
            line: { color: 'black' }
        };

        // Define plot layout
        let plotTopLayout = {
            title: 'diauxic behavior',
            xaxis: { title: 'time (h)' },
            yaxis: { title: 'concentration [mol/m³]' }
        };
        let plotBottomLayout = {
            title: 'enzyme concentration',
            xaxis: { title: 'time (h)' },
            yaxis: { title: 'enzyme concentration [mol/m³]' }
        };

        // Function to update plot
        function updatePlot() {
            let selectedPlot = document.getElementById('select-plot').value;
            if (selectedPlot === 'fed batch') {
                plotTopData.name = 'diauxic behavior';
                plotTopData.y = [X0, Glu0, Eta0];
                plotBottomData.name = 'enzyme concentration';
                plotBottomData.y = [E21, E11, E01];
                plotTopLayout.title = 'diauxic behavior';
                plotBottomLayout.title = 'enzyme concentration';
            } else if (selectedPlot === 'α') {
                plotTopData.name = 'glucose concentration';
                plotTopData.y = [Glu0];
                plotBottomData.name = 'fraction of respiring biomass';
                plotBottomData.y = [alphaMax];
                plotTopLayout.title = 'glucose concentration';
                plotBottomLayout.title = 'fraction of respiring biomass';
            }
            Plotly.react('plot-top', [plotTopData], plotTopLayout);
            Plotly.react('plot-bottom', [plotBottomData], plotBottomLayout);
        }

        // Function to update slider values
        function updateSliderValues() {
            alphaMax = parseFloat(document.getElementById('slider-alpha-max').value);
            tMin = parseFloat(document.getElementById('slider-t-min').value);
            tMax = parseFloat(document.getElementById('slider-t-max').value);
            enzymeControl = parseFloat(document.getElementById('slider-enzyme-control').value);
            feedRate = parseFloat(document.getElementById('slider-feed-rate').value);
            substrateFeedConc = parseFloat(document.getElementById('slider-substrate-feed-conc').value);
            feedTime = parseFloat(document.getElementById('slider-feed-time').value);
