            document.getElementById('slider-t-min-value').textContent = tMin;
            document.getElementById('slider-t-max-value').textContent = tMax;
            document.getElementById('slider-enzyme-control-value').textContent = enzymeControl;
            document.getElementById('slider-feed-rate-value').textContent = feedRate;
            document.getElementById('slider-substrate-feed-conc-value').textContent = substrateFeedConc;
            document.getElementById('slider-feed-time-value').textContent = feedTime;
        }

        // Function to update plot on slider change
        function updatePlotOnSliderChange() {
            updateSliderValues();
            feedTime = Math.min(feedTime, tMax);
            document.getElementById('slider-feed-time').max = tMax;
            document.getElementById('slider-feed-time').value = feedTime;
            updatePlot();
        }

        // Add event listeners to sliders
        document.getElementById('slider-alpha-max').addEventListener('input', updatePlotOnSliderChange);
        document.getElementById('slider-t-min').addEventListener('input', updatePlotOnSliderChange);
        document.getElementById('slider-t-max').addEventListener('input', updatePlotOnSliderChange);
        document.getElementById('slider-enzyme-control').addEventListener('input', updatePlotOnSliderChange);
        document.getElementById('slider-feed-rate').addEventListener('input', updatePlotOnSliderChange);
        document.getElementById('slider-substrate-feed-conc').addEventListener('input', updatePlotOnSliderChange);
        document.getElementById('slider-feed-time').addEventListener('input', updatePlotOnSliderChange);

        // Initialize plot
        updatePlot();
    </script>
</body>
