        <h1>Unsteady-State Diffusion in a Slab</h1>
        <p>
            The simulation models the diffusion of a solute within a slab with a thickness of 0.004 m. The slab is initially at a uniform concentration of 0.001, and the surface concentration is maintained at 0.002. The simulation allows the user to adjust the time and diffusivity parameters to observe the effects on the diffusion process.
        </p>
        <div id="control-panel">
            <div class="control-row">
                <label for="time-slider">time</label>
                <input type="range" id="time-slider" value="100" min="0" max="2500" step="1">
                <span id="time-value">100.</span>
            </div>
            <div class="control-row">
                <label for="diffusivity-slider">diffusivity in m<sup>2</sup>/s</label>
                <input type="range" id="diffusivity-slider" value="4e-9" min="1e-10" max="1e-8" step="1e-10">
                <span id="diffusivity-value">4.0 x 10⁻⁹</span>
            </div>
        </div>
        <div id="plot-div"></div>
    </div>

    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        // Initialize plot and sliders
        let timeSlider = document.getElementById('time-slider');
        let diffusivitySlider = document.getElementById('diffusivity-slider');
        let timeValueSpan = document.getElementById('time-value');
        let diffusivityValueSpan = document.getElementById('diffusivity-value');
        let plotDiv = document.getElementById('plot-div');

        // Initial plot settings
        let plotLayout = {
            xaxis: {
                title: 'position',
                range: [0, 0.004],
                tickformat: '.3f',
                dtick: 0.001,
                showgrid: true,
                minor: { showgrid: true }
            },
            yaxis: {
                title: 'concentration',
                range: [0.001, 0.0042],
                tickformat: '.4f',
                dtick: 0.0005,
                showgrid: true,
                minor: { showgrid: true }
            },
            margin: { l: 80, r: 20, t: 20, b: 50 },
            datarevision: 0
        };

        // Function to update plot
        function updatePlot() {
            let time = parseFloat(timeSlider.value);
            let diffusivity = parseFloat(diffusivitySlider.value);

            // Calculate concentration profile
            let x = [];
            let c = [];
            for (let i = 0; i < 200; i++) {
                let xValue = (i / 200) * 0.004;
                let cValue;
                if (time === 0) {
                    cValue = (1 + 250 * xValue) * 1e-3;
                } else {
                    let u = 0;
                    for (let n = 0; n < 100; n++) {
                        let k = ((n + 0.5) * Math.PI) / 0.004;
                        let B = (2 / 0.004) * 1e-3 * ((250 * (-1)**n) / k**2 - 3 / k);
                        u += B * Math.sin(k * xValue) * Math.exp(-k**2 * diffusivity * time);
                    }
                    cValue = u + 4e-3;
                }
                x.push(xValue);
                c.push(cValue);
            }

            // Update plot
            let plotData = [{ x, y: c }];
            Plotly.react(plotDiv, plotData, plotLayout, { transition: { duration: 0 } });
            plotLayout.datarevision++;
        }

        // Initialize plot with default values
        updatePlot();

        // Update plot on slider interaction
        timeSlider.addEventListener('input', updatePlot);
        diffusivitySlider.addEventListener('input', updatePlot);

        // Update slider value displays
        timeSlider.addEventListener('input', () => timeValueSpan.textContent = timeSlider.value);
        diffusivitySlider.addEventListener('input', () => diffusivityValueSpan.textContent = parseFloat(diffusivitySlider.value).toExponential(2));
    </script>
</body>
</html>
