    <script>
        let state = {
            funcKey: 'x',
            type: 'right',
            a: 5,
            n: 3
        };

        function setup() {
            createCanvas(800, 550);
            background(248, 248, 248);
            noStroke();
            textSize(18);
            textAlign(LEFT, TOP);
        }

        function draw() {
            background(248, 248, 248);
            let xMin = 0;
            let xMax = state.a;
            let yMin = 0;
            let yMax = 0;

            // Establish scale
            let scaleX = width / (xMax - xMin);
            let scaleY = height / (yMax - yMin);

            // Draw axes
            stroke(192, 192, 192);
            line(xMin * scaleX, height, xMax * scaleX, height);
            line(0, yMin * scaleY, 0, yMax * scaleY);

            // Draw function curve
            stroke(0);
            strokeWeight(2);
            let func = eval(`x => ${state.funcKey}`);
            beginShape();
            for (let x = xMin; x <= xMax; x += (xMax - xMin) / 100) {
                let y = func(x);
                vertex(x * scaleX, height - y * scaleY);
            }
            endShape();

            // Calculate and draw estimation shapes
            let deltaX = (xMax - xMin) / state.n;
            for (let i = 0; i < state.n; i++) {
                let x1 = xMin + i * deltaX;
                let x2 = x1 + deltaX;
                let y1 = func(x1);
                let y2 = func(x2);

                let color;
                if (state.type === 'left') {
                    color = y1 > y2 ? 'rgba(173, 216, 230, 0.7)' : 'rgba(144, 238, 144, 0.7)';
                } else if (state.type === 'right') {
                    color = y2 > y1 ? 'rgba(173, 216, 230, 0.7)' : 'rgba(144, 238, 144, 0.7)';
                } else if (state.type === 'midpoint') {
                    color = (y1 + y2) / 2 > y1 && (y1 + y2) / 2 > y2 ? 'rgba(173, 216, 230, 0.7)' : 'rgba(144, 238, 144, 0.7)';
                } else if (state.type === 'overestimate') {
                    color = Math.max(y1, y2) > y1 && Math.max(y1, y2) > y2 ? 'rgba(173, 216, 230, 0.7)' : 'rgba(144, 238, 144, 0.7)';
                } else if (state.type === 'underestimate') {
                    color = Math.min(y1, y2) < y1 && Math.min(y1, y2) < y2 ? 'rgba(173, 216, 230, 0.7)' : 'rgba(144, 238, 144, 0.7)';
                } else if (state.type === 'trapezoidal') {
                    color = (y1 + y2) / 2 > y1 && (y1 + y2) / 2 > y2 ? 'rgba(173, 216, 230, 0.7)' : 'rgba(144, 238, 144, 0.7)';
                }

                if (state.type === 'left' || state.type === 'right') {
                    fill(color);
                    rect(x1 * scaleX, height - y1 * scaleY, deltaX * scaleX, (y1 - y2) * scaleY);
                } else if (state.type === 'midpoint') {
                    fill(color);
                    rect(x1 * scaleX, height - (y1 + y2) / 2 * scaleY, deltaX * scaleX, (y2 - y1) * scaleY);
                } else if (state.type === 'overestimate') {
                    fill(color);
                    rect(x1 * scaleX, height - Math.max(y1, y2) * scaleY, deltaX * scaleX, Math.min(y1, y2) * scaleY);
                } else if (state.type === 'underestimate') {
                    fill(color);
                    rect(x1 * scaleX, height - Math.min(y1, y2) * scaleY, deltaX * scaleX, Math.max(y1, y2) * scaleY);
                } else if (state.type === 'trapezoidal') {
                    fill(color);
                    let y3 = func(x1 + deltaX / 2);
                    quad(x1 * scaleX, height - y1 * scaleY, x1 * scaleX + deltaX * scaleX / 2, height - y3 * scaleY, x2 * scaleX + deltaX * scaleX / 2, height - y3 * scaleY, x2 * scaleX, height - y2 * scaleY);
                }

                stroke(128, 128, 128);
                strokeWeight(1);
                if (state.type === 'left' || state.type === 'right') {
                    rect(x1 * scaleX, height - y1 * scaleY, deltaX * scaleX, (y1 - y2) * scaleY);
                } else if (state.type === 'midpoint') {
                    rect(x1 * scaleX, height - (y1 + y2) / 2 * scaleY, deltaX * scaleX, (y2 - y1) * scaleY);
                } else if (state.type === 'overestimate') {
                    rect(x1 * scaleX, height - Math.max(y1, y2) * scaleY, deltaX * scaleX, Math.min(y1, y2) * scaleY);
                } else if (state.type === 'underestimate') {
                    rect(x1 * scaleX, height - Math.min(y1, y2) * scaleY, deltaX * scaleX, Math.max(y1, y2) * scaleY);
                } else if (state.type === 'trapezoidal') {
                    let y3 = func(x1 + deltaX / 2);
                    quad(x1 * scaleX, height - y1 * scaleY, x1 * scaleX + deltaX * scaleX / 2, height - y3 * scaleY, x2 * scaleX + deltaX * scaleX / 2, height - y3 * scaleY, x2 * scaleX, height - y2 * scaleY);
                }
            }

            // Draw upper limit marker
            stroke(255, 0, 0);
            line(xMax * scaleX, height, xMax * scaleX, height - 10);
            fill(255, 0, 0);
            rect(xMax * scaleX - 5, height - 10, 10, 10);

            // Display calculations
            let estimatedArea = 0;
            let trueIntegral = 0;
            for (let i = 0; i < state.n; i++) {
                let x1 = xMin + i * deltaX;
                let x2 = x1 + deltaX;
                let y1 = func(x1);
                let y2 = func(x2);
                if (state.type === 'left') {
                    estimatedArea += (y1 - y2) * deltaX;
                } else if (state.type === 'right') {
                    estimatedArea += (y2 - y1) * deltaX;
                } else if (state.type === 'midpoint') {
                    estimatedArea += (y1 + y2) / 2 * deltaX;
                } else if (state.type === 'overestimate') {
                    estimatedArea += Math.max(y1, y2) * deltaX;
                } else if (state.type === 'underestimate') {
                    estimatedArea += Math.min(y1, y2) * deltaX;
                } else if (state.type === 'trapezoidal') {
                    let y3 = func(x1 + deltaX / 2);
                    estimatedArea += (y1 + y2 + 2 * y3) / 2 * deltaX;
                }
                trueIntegral += func(x1 + deltaX / 2) * deltaX;
            }
            estimatedArea = estimatedArea.toFixed(5);
            trueIntegral = trueIntegral.toFixed(5);
            fill(0);
            text(`estimated area: ${estimatedArea}`, 10, 20);
            text(`integral: ${trueIntegral}`, 10, 40);
        }

        function updateValueA() {
            state.a = document.getElementById('slider-a').value;
            document.getElementById('value-a').textContent = state.a.toFixed(2);
            redraw();
        }

        function updateValueN() {
