            redraw();
        }

        function updateType() {
            let type = document.querySelector('.type-btn.active').getAttribute('data-type');
            state.type = type;
            redraw();
        }

        document.getElementById('slider-a').addEventListener('input', updateValueA);
        document.getElementById('slider-n').addEventListener('input', updateValueN);
        document.querySelectorAll('.function-btn').forEach(function(button) {
            button.addEventListener('click', updateFunction);
        });
        document.querySelectorAll('.type-btn').forEach(function(button) {
            button.addEventListener('click', updateType);
        });
    </script>
