                <input type="range" id="slider-earth-field" min="0" max="0.0001" step="0.0000001" value="0.0000194">
                <span id="value-earth-field">1.94e-05</span>
            </div>
            <div>
                <label for="slider-radius">Radius (m)</label>
                <input type="range" id="slider-radius" min="0.1" max="1.0" step="0.01" value="0.5">
                <span id="value-radius">0.5</span>
            </div>
            <div>
                <label for="slider-turns">Number of turns</label>
                <input type="range" id="slider-turns" min="1" max="100" step="1" value="25">
                <span id="value-turns">25</span>
            </div>
            <button id="btn-reset">↻</button>
        </div>
        <div id="viz-container">
            <canvas id="p5-canvas"></canvas>
            <div id="axes">
                <div id="y-axis"></div>
                <div id="z-axis"></div>
                <div id="y-ticks"></div>
                <div id="z-ticks"></div>
            </div>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let current = 0.431;
        let earthField = 0.0000194;
        let radius = 0.5;
        let turns = 25;

        function setup() {
            createCanvas(800, 600);
            background(220);
        }

        function draw() {
            background(220);
            drawVectorField();
        }

        function drawVectorField() {
            let gridSize = 25;
            let minMag = Infinity;
            let maxMag = -Infinity;
            let grid = [];
            for (let y = -0.5; y <= 0.5; y += 0.1) {
                for (let z = -0.5; z <= 0.5; z += 0.1) {
                    let B_total = calculateB(y, z);
                    let mag = B_total.mag();
                    grid.push({x: y, y: z, B: B_total});
                    minMag = min(minMag, mag);
                    maxMag = max(maxMag, mag);
                }
            }
            let colorRange = maxMag - minMag;
            for (let i = 0; i < grid.length; i++) {
                let p = grid[i];
                let mag = p.B.mag();
                let hue = map(mag, minMag, maxMag, 270, 60);
                let r = p5.Color.hsb(hue, 100, 100);
                stroke(r);
                let B = p.B;
                let stepSize = 0.01;
                let direction = B.copy().normalize();
                let p_next = p5.Vector.add(p, direction.mult(stepSize));
                line(p.x, p.y, p_next.x, p_next.y);
                if (i % 10 === 0) {
                    let arrowLength = 0.01;
                    let tipX = p_next.x - direction.x * arrowLength;
                    let tipY = p_next.y - direction.y * arrowLength;
                    let baseX = p_next.x + direction.x * arrowLength;
                    let baseY = p_next.y + direction.y * arrowLength;
                    line(p_next.x, p_next.y, tipX, tipY);
                    line(p_next.x, p_next.y, baseX, baseY);
                }
            }
            for (let y = -0.5; y <= 0.5; y += 0.2) {
                let x = 0;
                let yTick = createVector(x, y);
                let zTick = createVector(x, y);
                line(x, y, x, y + 0.01);
                line(x, y, x - 0.01, y);
                fill(255, 0, 0);
                textSize(12);
                textAlign(CENTER, CENTER);
                text(y.toFixed(2), x, y + 0.015);
                text(-y.toFixed(2), x, y - 0.015);
            }
            for (let z = -0.5; z <= 0.5; z += 0.2) {
                let x = 0;
                let zTick = createVector(x, z);
                let yTick = createVector(x, z);
                line(x, z, x, z + 0.01);
                line(x, z, x - 0.01, z);
                fill(255, 0, 0);
                textSize(12);
                textAlign(CENTER, CENTER);
                text(z.toFixed(2), x, z + 0.015);
                text(-z.toFixed(2), x, z - 0.015);
            }
        }

        function calculateB(y, z) {
            let I = current;
            let B_z_earth = earthField;
            let R = radius;
            let N = turns;
            let μ₀ = 4 * Math.PI * Math.pow(10, -7);
            let B_coils = createVector(0, 0);
            let B_earth = createVector(0, B_z_earth);
            for (let i = 0; i < 2; i++) {
                let z_offset = i === 0 ? -R / 2 : R / 2;
                let zp = z - z_offset;
                let m = (4 * R * y) / ((R + y) ** 2 + zp ** 2);
                let common_factor_1 = (μ₀ * I * N) / (2 * Math.PI);
                let common_factor_2 = 1 / Math.sqrt((R + y) ** 2 + zp ** 2);
                let B_y = common_factor_1 * (zp / y) * common_factor_2 * [((R ** 2 + y ** 2 + zp ** 2) / ((R - y) ** 2 + zp ** 2)) * ellipticE(m) - ellipticK(m)];
                let B_z = common_factor_1 * common_factor_2 * [ellipticK(m) - ((R ** 2 - y ** 2 - zp ** 2) / ((R - y) ** 2 + zp ** 2)) * ellipticE(m)];
                B_coils.add(createVector(B_y, B_z));
            }
            return B_coils.add(B_earth);
        }

        function ellipticK(m) {
            let a = 1;
            let b = 1;
            let c = 1;
            let s = 1;
            let t = 1;
            let e = 1;
            let d = 1;
            let k = 1;
            let n = 0;
            let m2 = m ** 2;
            while (true) {
                let q = (1 - m2) / (1 + m2);
                let r = (q + 1) / (q + 2);
                let d1 = Math.sqrt((r + 1) / (r - 1));
                let d2 = Math.sqrt((1 + d1) / (1 - d1));
                e = Math.sqrt((1 + d1) / 2);
                s = Math.sqrt((1 + d2) / 2);
                t = Math.sqrt((1 - d2) / 2);
                a = (a + b) / 2;
                b = Math.sqrt(a * b);
                c = a * b / (a + b);
                d = Math.sqrt((1 + e) / (1 - e));
                k = Math.sqrt((1 + d) / (1 - d));
                m2 = m2 * (k ** 2);
                n++;
                if (Math.abs(m2) < 1e-12) {
                    break;
                }
            }
            return Math.PI * (a - b) / (a + b);
        }

        function ellipticE(m) {
            let a = 1;
            let b = 1;
            let c = 1;
            let s = 1;
            let t = 1;
            let e = 1;
            let d = 1;
            let k = 1;
            let n = 0;
            let m2 = m ** 2;
            while (true) {
                let q = (1 - m2) / (1 + m2);
                let r = (q + 1) / (q + 2);
                let d1 = Math.sqrt((r + 1) / (r - 1));
                let d2 = Math.sqrt((1 + d1) / (1 - d1));
                e = Math.sqrt((1 + d1) / 2);
                s = Math.sqrt((1 + d2) / 2);
                t = Math.sqrt((1 - d2) / 2);
                a = (a + b) / 2;
                b = Math.sqrt(a * b);
                c = a * b / (a + b);
                d = Math.sqrt((1 + e) / (1 - e));
                k = Math.sqrt((1 + d) / (1 - d));
                m2 = m2 * (k ** 2);
                n++;
                if (Math.abs(m2) < 1e-12) {
                    break;
                }
            }
            return Math.PI * (a + b) / (a + b) - Math.PI / 2;
        }

        let sliderCurrent = document.getElementById('slider-current');
        let sliderEarthField = document.getElementById('slider-earth-field');
        let sliderRadius = document.getElementById('slider-radius');
