    </style>
</head>
<body>
    <div id="control-panel">
        <div>
            <label for="slider-convolver">convolver</label>
            <button>-</button>
            <input type="range" id="slider-convolver" value="2" min="1" max="5" step="1">
            <button id="convolver-plus-btn">+</button>
        </div>
        <div>
            <label for="slider-image-size">image size</label>
            <button>-</button>
            <input type="range" id="slider-image-size" value="24" min="10" max="40" step="1">
            <button id="image-size-plus-btn">+</button>
        </div>
        <div>
            <label for="input-letter">pick a letter:</label>
            <input type="text" id="input-letter" value="a" maxlength="1">
        </div>
        <div id="radio-group">
            <input type="radio" id="radio-shadow" name="filter" value="shadow" checked>
            <label for="radio-shadow" class="active">shadow</label>
            <input type="radio" id="radio-blur" name="filter" value="blur">
            <label for="radio-blur">blur</label>
        </div>
    </div>
    <div id="visualization-area">
        <div>
            <p>before</p>
            <div id="canvas-before-container" style="width: 200px; height: 200px;"></div>
        </div>
        <div>
            <p>after</p>
            <div id="canvas-after-container" style="width: 200px; height: 200px;"></div>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let sketch = function(p) {
            let convolver = 2;
            let imageSize = 24;
            let letter = 'a';
            let filter = 'shadow';

            p.setup = function() {
                p.createCanvas(400, 200);
                p.frameRate(30);
                p.background(255);
                updateVisualization();
            }

            p.draw = function() {
                // nothing to do here
            }

            function updateVisualization() {
                convolver = parseInt(document.getElementById('slider-convolver').value);
                imageSize = parseInt(document.getElementById('slider-image-size').value);
                letter = document.getElementById('input-letter').value;
                filter = document.querySelector('input[name="filter"]:checked').value;

                let kernel = [];
                let D = 2 * convolver + 1;
                for (let i = 0; i < D; i++) {
                    kernel[i] = [];
                    for (let j = 0; j < D; j++) {
                        kernel[i][j] = 0;
                    }
                }

                if (filter === 'blur') {
                    for (let i = 0; i < D; i++) {
                        for (let j = 0; j < D; j++) {
                            kernel[i][j] = 1.0 / (D * D);
                        }
                    }
                } else if (filter === 'shadow') {
                    let k = convolver;
                    for (let i = 0; i < D; i++) {
                        for (let j = 0; j < D; j++) {
                            if (i >= k && i <= D - k - 1 && j >= k && j <= D - k - 1) {
                                kernel[i][j] = 1.0 / (k * k);
                            }
                        }
                    }
                    for (let i = 0; i < D; i++) {
                        for (let j = 0; j < D; j++) {
                            kernel[i][j] /= Math.max(...kernel.map(row => Math.max(...row)));
                        }
                    }
                }

                let beforeGrid = [];
                for (let i = 0; i < imageSize; i++) {
                    beforeGrid[i] = [];
                    for (let j = 0; j < imageSize; j++) {
                        beforeGrid[i][j] = 0;
                    }
                }

                let x = imageSize / 2;
                let y = imageSize / 2;
                p.textSize(imageSize * 0.8);
                p.textAlign(p.CENTER, p.CENTER);
                p.fill(0);
                p.text(letter, x, y);

                for (let i = 0; i < imageSize; i++) {
                    for (let j = 0; j < imageSize; j++) {
                        let d = dist(x, y, j, i);
                        if (d < imageSize / 2) {
                            beforeGrid[i][j] = 1;
                        }
                    }
                }

                let afterGrid = [];
                for (let i = 0; i < imageSize; i++) {
                    afterGrid[i] = [];
                    for (let j = 0; j < imageSize; j++) {
                        afterGrid[i][j] = 0;
                    }
                }

                for (let i = 0; i < imageSize; i++) {
                    for (let j = 0; j < imageSize; j++) {
                        let sum = 0;
                        for (let k = 0; k < D; k++) {
                            for (let l = 0; l < D; l++) {
                                let ni = i + k - D / 2;
                                let nj = j + l - D / 2;
                                if (ni >= 0 && ni < imageSize && nj >= 0 && nj < imageSize) {
                                    sum += kernel[k][l] * beforeGrid[ni][nj];
                                }
                            }
                        }
                        afterGrid[i][j] = sum;
                    }
                }

                p.background(255);
                p.noStroke();
                for (let i = 0; i < imageSize; i++) {
                    for (let j = 0; j < imageSize; j++) {
                        if (beforeGrid[i][j] === 1) {
                            p.fill(0);
                            p.rect(j * 200 / imageSize, i * 200 / imageSize, 200 / imageSize, 200 / imageSize);
                        }
                    }
                }
                p.stroke(0);
                for (let i = 0; i < imageSize; i++) {
                    for (let j = 0; j < imageSize; j++) {
                        let c = color(255 * (1 - afterGrid[i][j]));
                        p.fill(c);
                        p.rect(j * 200 / imageSize, i * 200 / imageSize, 200 / imageSize, 200 / imageSize);
                    }
                }
            }

            p.windowResized = function() {
                p.resizeCanvas(p.windowWidth, p.windowHeight);
            }

            p.keyPressed = function() {
                if (p.key === 'ArrowUp' || p.key === 'ArrowDown') {
                    let alphabet = 'abcdefghijklmnopqrstuvwxyz';
                    let index = alphabet.indexOf(letter);
                    if (p.key === 'ArrowUp') {
                        index = (index + 1) % alphabet.length;
                    } else {
                        index = (index - 1 + alphabet.length) % alphabet.length;
                    }
                    letter = alphabet[index];
                    document.getElementById('input-letter').value = letter;
                    updateVisualization();
                }
            }

            p.input = function() {
                updateVisualization();
            }
        }

        let myp5 = new p5(sketch, 'canvas-before-container');
        let myp5after = new p5(sketch, 'canvas-after-container');
    </script>
</body>
</html>
