```javascript
// script.js
let proteinData = generateProteinData(400);
let currentView = 'crystallography';
let residueA = 1;
let residueX = 50;
let residueY = 52;
let rmsd = 4.9;
let grayLevel = 0.5;
let plotSize = 400;

function generateProteinData(numResidues) {
    let protein = [];
    for (let i = 0; i < numResidues; i++) {
        let residue = {
            resName: getAminoAcidType(),
            resSeq: i + 1,
            atoms: []
        };
        for (let j = 0; j < 5; j++) {
            let atom = {
                atomName: getAtomName(),
                x: Math.random() * 10 - 5,
                y: Math.random() * 10 - 5,
                z: Math.random() * 10 - 5
            };
            residue.atoms.push(atom);
        }
        protein.push(residue);
    }
    return protein;
}

function getAminoAcidType() {
    let aminoAcids = ['A', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'K', 'L', 'M', 'N', 'P', 'Q', 'R', 'S', 'T', 'V', 'W', 'Y'];
    return aminoAcids[Math.floor(Math.random() * aminoAcids.length)];
}

function getAtomName() {
    let atoms = ['CA', 'N', 'C', 'O', 'CB'];
    return atoms[Math.floor(Math.random() * atoms.length)];
}

function initThreeJs() {
    let scene = new THREE.Scene();
    let camera = new THREE.PerspectiveCamera(75, plotSize / plotSize, 0.1, 1000);
    let renderer = new THREE.WebGLRenderer({
        canvas: document.getElementById('canvas'),
        antialias: true
    });
    renderer.setSize(plotSize, plotSize);
    let controls = new THREE.OrbitControls(camera, renderer.domElement);
    scene.background = new THREE.Color(0xffffff);
    let ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
    let directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
    directionalLight.position.set(10, 10, 10);
    scene.add(ambientLight);
    scene.add(directionalLight);
    let geometry = new THREE.SphereGeometry(0.5, 16, 16);
    let material = new THREE.MeshLambertMaterial({ color: 0x0000ff });
    let atomMeshes = [];
    for (let i = 0; i < proteinData.length; i++) {
        for (let j = 0; j < proteinData[i].atoms.length; j++) {
            let mesh = new THREE.Mesh(geometry, material);
            mesh.position.set(proteinData[i].atoms[j].x, proteinData[i].atoms[j].y, proteinData[i].atoms[j].z);
            scene.add(mesh);
            atomMeshes.push(mesh);
        }
    }
    let boxHelper = new THREE.BoxHelper(new THREE.BoxGeometry(10, 10, 10), 0x888888);
    scene.add(boxHelper);
    camera.position.set(0, 0, 10);
    controls.target.set(0, 0, 0);
    controls.update();
    animate();
}

function animate() {
    requestAnimationFrame(animate);
    let grayLevelValue = document.getElementById('slider-gray-level').value;
    let material = new THREE.MeshLambertMaterial({ color: 0x0000ff * grayLevelValue });
    for (let i = 0; i < proteinData.length; i++) {
        for (let j = 0; j < proteinData[i].atoms.length; j++) {
            if (proteinData[i].atoms[j].atomName !== 'CA') {
                atomMeshes[i * 5 + j].material = material;
            }
        }
    }
    let residueAX = document.getElementById('slider-residue-x').value;
    let residueAY = document.getElementById('slider-residue-y').value;
    let residueAIndex = residueAX - 1;
    let residueAXMesh = atomMeshes[residueAIndex * 5];
    let residueAYMesh = atomMeshes[residueAIndex * 5 + 1];
    residueAXMesh.material = new THREE.MeshLambertMaterial({ color: 0xff0000 });
    residueAYMesh.material = new THREE.MeshLambertMaterial({ color: 0xff0000 });
    let residueYIndex = residueY - 1;
    let residueYMesh = atomMeshes[residueYIndex * 5];
    residueYMesh.material = new THREE.MeshLambertMaterial({ color: 0xff0000 });
    renderer.render(scene, camera);
}

function initP5() {
    let canvas = document.getElementById('canvas');
    let p5Instance = new p5((sketch) => {
        let rmsdValue = document.getElementById('slider-rmsd').value;
        let plotSizeValue = document.getElementById('slider-plot-size').value;
        let canvasWidth = plotSizeValue;
        let canvasHeight = plotSizeValue;
        let originX = canvasWidth / 2;
        let originY = canvasHeight / 2;
        let axisLength = 100;
        let tickSpacing = 100;
        let labelSpacing = 50;
        let rmsd = rmsdValue;
        let distanceMatrix = generateDistanceMatrix();
        sketch.setup = () => {
            sketch.createCanvas(canvasWidth, canvasHeight);
            sketch.background(255);
            sketch.stroke(0);
            sketch.strokeWeight(1);
            sketch.line(originX, 0, originX, canvasHeight);
            sketch.textSize(12);
            sketch.textAlign(sketch.LEFT, sketch.BOTTOM);
            for (let i = 0; i <= axisLength; i += tickSpacing) {
                sketch.line(originX - i, 0, originX - i, 10);
                sketch.text(i.toString(), originX - i - 5, 15);
            }
            for (let i = 0; i <= axisLength; i += tickSpacing) {
                sketch.line(0, originY + i, 10, originY + i);
                sketch.text(i.toString(), -5, originY + i + 5);
            }
            sketch.text('Residue Index', originX - labelSpacing, 15);
            sketch.text('Residue Index', -5, originY + axisLength + 5);
        }
        sketch.draw = () => {
            sketch.background(255);
            sketch.stroke(0);
            sketch.strokeWeight(1);
            sketch.line(originX, 0, originX, canvasHeight);
            sketch.textSize(12);
            sketch.textAlign(sketch.LEFT, sketch.BOTTOM);
            for (let i = 0; i <= axisLength; i += tickSpacing) {
                sketch.line(originX - i, 0, originX - i, 10);
                sketch.text(i.toString(), originX - i - 5, 15);
            }
            for (let i = 0; i <= axisLength; i += tickSpacing) {
                sketch.line(0, originY + i, 10, originY + i);
                sketch.text(i.toString(), -5, originY + i + 5);
            }
            sketch.text('Residue Index', originX - labelSpacing, 15);
            sketch.text('Residue Index', -5, originY + axisLength + 5);
            for (let i = 0; i < proteinData.length; i++) {
                for (let j = 0; j < proteinData[i].atoms.length; j++) {
                    let x = originX - (i + 1) * tickSpacing;
                    let y = originY + (proteinData[i].atoms[j].z + 5) * tickSpacing;
                    if (distanceMatrix[i][j] <= rmsd) {
                        sketch.fill(0);
                        sketch.noStroke();
                        sketch.ellipse(x, y, 2, 2);
                    }
                }
            }
            sketch.fill(255, 0, 0);
            sketch.stroke(255, 0, 0);
            sketch.strokeWeight(2);
            sketch.line(originX - (residueX - 1) * tickSpacing, 0, originX - (residueX - 1) * tickSpacing, canvasHeight);
            sketch.line(0, originY + (residueY - 1) * tickSpacing, canvasWidth, originY + (residueY - 1) * tickSpacing);
        }
    }, canvas);
}

function generateDistanceMatrix() {
    let distanceMatrix = [];
    for (let i = 0; i < proteinData.length; i++) {
        let row = [];
        for (let j = 0; j < proteinData.length; j++) {
            let distance = Math.sqrt(Math.pow(proteinData[i].atoms[0].x - proteinData[j].atoms[0].x, 2) + Math.pow(proteinData[i].atoms[0].y - proteinData[j].atoms[0].y, 2) + Math.pow(proteinData[i].atoms[0].z - proteinData[j].atoms[0].z, 2));
            row.push(distance);
        }
        distanceMatrix.push(row);
    }
    return distanceMatrix;
}

initThreeJs();
initP5();

document.getElementById('btn-crystallography').addEventListener('click', () => {
