                <label for="radio-chars-no">no</label>
            </div>
        </div>
        <div id="viz-container">
            <div id="header-section">
                <p id="original-text">MISSISSIPPI_RIVER</p>
                <p id="encoded-text">1100000101000101001001000011111010011011110101</p>
            </div>
            <div id="main-content">
                <div id="info-panel">
                    <p id="explanation-text">Once at the top, the total frequency becomes 17, which is the total number of characters.</p>
                    <div id="encoding-table">
                        <p>I = 00</p>
                        <p>S = 01</p>
                        <p>P = 100</p>
                        <p>R = 101</p>
                        <p>M = 1100</p>
                        <p>V = 1101</p>
                        <p>E = 1110</p>
                        <p>_ = 1111</p>
                    </div>
                </div>
                <div id="canvas-area">
                    <canvas id="canvas" width="800" height="500"></canvas>
                </div>
            </div>
        </div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/dist/p5.js"></script>
    <script>
        let currentTier = 5;
        let showBinary = false;
        let showNodeChars = true;
        let treeData = [
            { id: 'I', label: 'I', freq: 5, tier: 1, pos: { x: 100, y: 450 }, children: [] },
            { id: 'S', label: 'S', freq: 4, tier: 1, pos: { x: 180, y: 450 }, children: [] },
            { id: 'P', label: 'P', freq: 2, tier: 1, pos: { x: 260, y: 450 }, children: [] },
            { id: 'R', label: 'R', freq: 2, tier: 1, pos: { x: 340, y: 450 }, children: [] },
            { id: 'M', label: 'M', freq: 1, tier: 1, pos: { x: 420, y: 450 }, children: [] },
            { id: 'V', label: 'V', freq: 1, tier: 1, pos: { x: 500, y: 450 }, children: [] },
            { id: 'E', label: 'E', freq: 1, tier: 1, pos: { x: 580, y: 450 }, children: [] },
            { id: '_', label: '_', freq: 1, tier: 1, pos: { x: 660, y: 450 }, children: [] },
            { id: 'IS', label: 'IS', freq: 9, tier: 2, pos: { x: 140, y: 350 }, children: ['I', 'S'] },
            { id: 'PR', label: 'PR', freq: 4, tier: 2, pos: { x: 300, y: 350 }, children: ['P', 'R'] },
            { id: 'MV', label: 'MV', freq: 2, tier: 2, pos: { x: 460, y: 350 }, children: ['M', 'V'] },
            { id: 'E_', label: 'E_', freq: 2, tier: 2, pos: { x: 620, y: 350 }, children: ['E', '_'] },
            { id: 'MVE_', label: 'MVE_', freq: 4, tier: 3, pos: { x: 540, y: 250 }, children: ['MV', 'E_'] },
            { id: 'PRMVE_', label: 'PRMVE_', freq: 8, tier: 4, pos: { x: 420, y: 150 }, children: ['PR', 'MVE_'] },
            { id: 'ROOT', label: 'ISPRMVE_', freq: 17, tier: 5, pos: { x: 280, y: 50 }, children: ['IS', 'PRMVE_'] }
        ];

        function draw() {
            let canvas = document.getElementById('canvas');
            let ctx = canvas.getContext('2d');
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            for (let i = 0; i < treeData.length; i++) {
                let node = treeData[i];
                if (node.tier <= currentTier && (node.children.length === 0 || node.children[0] !== undefined)) {
                    ctx.fillStyle = '#FEFDD5';
                    ctx.strokeStyle = '#C70000';
                    ctx.lineWidth = 2;
                    ctx.roundRect(node.pos.x, node.pos.y, 100, 50, 10);
                    ctx.stroke();
                    ctx.fill();

                    if (showNodeChars && node.children.length === 0) {
                        ctx.fillStyle = '#000';
                        ctx.font = '18px Arial';
                        ctx.textAlign = 'center';
                        ctx.textBaseline = 'middle';
                        ctx.fillText(node.label + '(' + node.freq + ')', node.pos.x, node.pos.y);
                    } else {
                        ctx.fillStyle = '#000';
                        ctx.font = '18px Arial';
                        ctx.textAlign = 'center';
                        ctx.textBaseline = 'middle';
                        ctx.fillText('(' + node.freq + ')', node.pos.x, node.pos.y);
                    }
                }
            }

            for (let i = 0; i < treeData.length; i++) {
                let node = treeData[i];
                if (node.tier <= currentTier && (node.children.length > 0 || node.children[0] !== undefined)) {
                    let parent = treeData.find(n => n.children.includes(node.id));
                    if (parent !== undefined) {
                        ctx.strokeStyle = '#C70000';
                        ctx.lineWidth = 2;
                        ctx.beginPath();
                        ctx.moveTo(parent.pos.x + 50, parent.pos.y + 25);
                        ctx.lineTo(node.pos.x + 50, node.pos.y + 25);
                        ctx.stroke();

                        if (showBinary) {
                            let childIndex = node.children.indexOf(node.id);
                            if (childIndex === 0) {
                                ctx.fillStyle = '#000';
                                ctx.font = '18px Arial';
                                ctx.textAlign = 'center';
                                ctx.textBaseline = 'middle';
                                ctx.fillText('0', (parent.pos.x + node.pos.x) / 2, (parent.pos.y + node.pos.y) / 2 + 10);
                            } else {
                                ctx.fillStyle = '#000';
                                ctx.font = '18px Arial';
                                ctx.textAlign = 'center';
                                ctx.textBaseline = 'middle';
                                ctx.fillText('1', (parent.pos.x + node.pos.x) / 2, (parent.pos.y + node.pos.y) / 2 + 10);
                            }
                        }
                    }
                }
            }
        }

        function roundRect(x, y, w, h, r) {
            ctx.beginPath();
            ctx.moveTo(x + r, y);
            ctx.lineTo(x + w - r, y);
            ctx.quadraticCurveTo(x + w, y, x + w, y + r);
            ctx.lineTo(x + w, y + h - r);
            ctx.quadraticCurveTo(x + w, y + h, x + w - r, y + h);
            ctx.lineTo(x + r, y + h);
            ctx.quadraticCurveTo(x, y + h, x, y + h - r);
            ctx.lineTo(x, y + r);
            ctx.quadraticCurveTo(x, y, x + r, y);
            ctx.closePath();
        }

        document.getElementById('btn-tier-1').addEventListener('click', () => {
            currentTier = 1;
            draw();
        });

        document.getElementById('btn-tier-2').addEventListener('click', () => {
            currentTier = 2;
            draw();
        });

        document.getElementById('btn-tier-3').addEventListener('click', () => {
            currentTier = 3;
            draw();
        });

        document.getElementById('btn-tier-4').addEventListener('click', () => {
            currentTier = 4;
            draw();
        });

        document.getElementById('btn-tier-5').addEventListener('click', () => {
            currentTier = 5;
            draw();
        });

        document.getElementById('checkbox-binary').addEventListener('change', () => {
            showBinary = !showBinary;
            draw();
        });

        document.getElementById('radio-chars-yes').addEventListener('change', () => {
            showNodeChars = true;
            draw();
        });

        document.getElementById('radio-chars-no').addEventListener('change', () => {
            showNodeChars = false;
            draw();
        });

        draw();
    </script>
</body>
</html>
