    <div id="control-panel">
        <label for="slider-n">n:</label>
        <input type="range" id="slider-n" min="1" max="20" step="1" value="1">
        <input type="number" id="input-n" min="1" max="20" step="1" value="1">
    </div>
    <div id="visualization-area">
        <div id="function-plot-container">
            <canvas id="canvas-function" width="400" height="200"></canvas>
            <div id="formula-label">$$y = \frac{2}{x}$$</div>
        </div>
        <div id="sum-plot-container">
            <canvas id="canvas-sum" width="200" height="200"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <script>
        let n = 1;
        const colorArray = [
            '#ffff80', '#98fb98', '#7fffd4', '#00ffff', '#87cefa', '#6495ed', '#9370db', '#8a2be2',
            '#6a5acd', '#7a288a', '#660066', '#4c0080', '#330066', '#220066', '#110066', '#000066'
        ];

        function setupFunctionPlot() {
            createCanvas(400, 200);
            background(220);
            noLoop();
        }

        function drawFunctionPlot() {
            background(220);
            stroke(0);
            strokeWeight(2);
            line(0, height, width, 0);
            for (let x = 1; x <= width; x++) {
                let y = map(x, 0, width, 0, 2.2);
                line(x, y, x, 0);
                if (x % 1 === 0) {
                    fill(0);
                    noStroke();
                    text(nf(x), x - 10, 20);
                }
            }
            stroke(0, 0, 255);
            strokeWeight(2);
            beginShape();
            for (let k = 1; k <= n; k++) {
                let x1 = map(k, 1, n, 0, width);
                let y1 = map(2 / (k + 1), 0, 2.2, height, 0);
                let x2 = map(k + 1, 1, n, 0, width);
                let y2 = map(2 / k, 0, 2.2, height, 0);
                vertex(x1, y1);
                vertex(x2, y2);
                if (k <= 3) {
                    stroke(128);
                    strokeWeight(1);
                    line(x1, height, x1, y1);
                }
                fill(colorArray[k - 1]);
                noStroke();
                rect(x1, y1, 1, y2 - y1);
            }
            endShape();
        }

        function setupSummationPlot() {
            createCanvas(200, 200);
            background(220);
            noLoop();
        }

        function drawSummationPlot() {
            background(220);
            stroke(0);
            strokeWeight(2);
            line(0, height, width, 0);
            for (let y = 1; y <= height; y++) {
                let x = map(y, 0, height, 0, 1.5);
                line(0, y, x, y);
                if (y % 1 === 0) {
                    fill(0);
                    noStroke();
                    text(nf(y), 10, y + 10);
                }
            }
            stroke(0, 0, 255);
            strokeWeight(2);
            let y_stack = 0;
            beginShape();
            for (let k = n; k >= 1; k--) {
                let h = map(2 / k - 2 / (k + 1), 0, 2.2, 0, height);
                let y = map(h, 0, height, 0, height);
                let x = 0;
                fill(colorArray[k - 1]);
                noStroke();
                rect(x, y_stack, 1, y - y_stack);
                y_stack = y;
            }
            endShape();
        }

        function updateN() {
            n = parseInt(document.getElementById('input-n').value);
            document.getElementById('slider-n').value = n;
            drawFunctionPlot();
            drawSummationPlot();
        }

        function setup() {
            setupFunctionPlot();
            setupSummationPlot();
            document.getElementById('slider-n').addEventListener('input', updateN);
            document.getElementById('input-n').addEventListener('input', updateN);
        }

        function draw() {
            drawFunctionPlot();
            drawSummationPlot();
        }

        setup();
    </script>
</body>
</html>
