            <div id="plot-container"></div>
        </div>
    </div>

    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        // Global variables
        let currentModel = 'monod';
        let currentView = 'bifurcation';

        // Initialize sliders and value displays
        document.getElementById('slider-x2f').value = 4.0;
        document.getElementById('slider-umax').value = 0.6;
        document.getElementById('slider-km').value = 0.12;
        document.getElementById('slider-ds').value = 0.6;
        document.getElementById('value-x2f').innerText = 4.0;
        document.getElementById('value-umax').innerText = 0.6;
        document.getElementById('value-km').innerText = 0.12;
        document.getElementById('value-ds').innerText = 0.6;

        // Hide the k1 slider
        document.querySelector('.slider-container:nth-child(3)').style.display = 'block';
        document.querySelector('.slider-container:nth-child(4)').style.display = 'none';

        // Set active classes on buttons
        document.getElementById('btn-monod').classList.add('active');
        document.getElementById('btn-bifurcation').classList.add('active');

        // Function to update plot
        function updatePlot() {
            if (currentModel === 'monod') {
                drawBifurcationDiagram();
            } else {
                drawNontrivialStatePlot();
            }
        }

        // Function to draw bifurcation diagram
        function drawBifurcationDiagram() {
            // Create plot data
            let data = [
                {
                    x: [],
                    y: [],
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Stable',
                    line: { color: 'blue' }
                },
                {
                    x: [],
                    y: [],
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Unstable',
                    line: { color: 'red', dash: 'dash' }
                },
                {
                    x: [],
                    y: [],
                    type: 'scatter',
                    mode: 'markers',
                    name: 'Intersection points',
                    marker: {
                        color: 'magenta',
                        size: 10
                    }
                },
                {
                    x: [],
                    y: [],
                    type: 'scatter',
                    mode: 'markers',
                    name: 'Trivial steady state',
                    marker: {
                        color: 'green',
                        size: 10
                    }
                }
            ];

            // Generate x values
            let x = Array.from({ length: 100 }, (_, i) => i / 100);

            // Calculate D values
            let D = [];
            for (let i = 0; i < x.length; i++) {
                let D_i = (document.getElementById('slider-umax').value * document.getElementById('slider-x2f').value) / (document.getElementById('slider-km').value + document.getElementById('slider-x2f').value);
                D.push(D_i);
            }

            // Add data to plot
            data[0].x = x;
            data[0].y = D;
            data[1].x = x;
            data[1].y = D;
            data[2].x = [document.getElementById('slider-ds').value];
            data[2].y = [document.getElementById('slider-x2f').value];
            data[3].x = [document.getElementById('slider-ds').value];
            data[3].y = [document.getElementById('slider-x2f').value];

            // Create plot layout
            let layout = {
                title: 'Bifurcation Diagram',
                xaxis: {
                    title: 'Dilution rate',
                    range: [0, 1.0]
                },
                yaxis: {
                    title: 'X₂',
                    range: [0, 6.0]
                },
                shapes: [
                    {
                        type: 'line',
                        x0: document.getElementById('slider-ds').value,
                        x1: document.getElementById('slider-ds').value,
                        y0: 0,
                        y1: document.getElementById('slider-x2f').value,
                        line: {
                            color: 'gray',
                            dash: 'dashdot'
                        }
                    }
                ]
            };

            // Render plot
            Plotly.newPlot('plot-container', data, layout);
        }

        // Function to draw nontrivial steady state plot
        function drawNontrivialStatePlot() {
            // Create plot data
            let data = [
                {
                    x: [],
                    y: [],
                    type: 'scatter',
                    mode: 'lines',
                    name: 'μ(x₂)',
                    line: { color: 'blue' }
                },
                {
                    x: [],
                    y: [],
                    type: 'scatter',
                    mode: 'markers',
                    name: 'Intersection points',
                    marker: {
                        color: 'magenta',
                        size: 10
                    }
                },
                {
                    x: [],
                    y: [],
                    type: 'scatter',
                    mode: 'markers',
                    name: 'Trivial steady state',
                    marker: {
                        color: 'green',
                        size: 10
                    }
                }
            ];

            // Generate x values
            let x = Array.from({ length: 100 }, (_, i) => i / 100);

            // Calculate μ values
            let μ = [];
            for (let i = 0; i < x.length; i++) {
                let μ_i = (document.getElementById('slider-umax').value * x[i]) / (document.getElementById('slider-km').value + x[i] + (document.getElementById('slider-k1').value * x[i] ** 2));
                μ.push(μ_i);
            }

            // Add data to plot
            data[0].x = x;
            data[0].y = μ;
            data[1].x = [document.getElementById('slider-ds').value];
            data[1].y = [document.getElementById('slider-x2f').value];
            data[2].x = [document.getElementById('slider-ds').value];
            data[2].y = [document.getElementById('slider-x2f').value];

            // Create plot layout
            let layout = {
                title: 'Nontrivial Steady State Plot',
                xaxis: {
                    title: 'Substrate concentration',
                    range: [0, 7]
                },
                yaxis: {
                    title: 'Specific growth rate coefficient',
                    range: [0, 1]
                },
                shapes: [
                    {
                        type: 'rect',
                        x0: 0,
                        x1: document.getElementById('slider-ds').value,
                        y0: 0,
                        y1: 1,
                        fillcolor: 'lightcoral',
                        opacity: 0.5
                    }
                ]
            };

            // Render plot
            Plotly.newPlot('plot-container', data, layout);
        }

        // Add event listeners to buttons and sliders
        document.getElementById('btn-monod').addEventListener('click', () => {
            currentModel = 'monod';
            document.getElementById('btn-monod').classList.add('active');
            document.getElementById('btn-si').classList.remove('active');
            document.querySelector('.slider-container:nth-child(4)').style.display = 'none';
            updatePlot();
        });

        document.getElementById('btn-si').addEventListener('click', () => {
            currentModel = 'si';
            document.getElementById('btn-si').classList.add('active');
            document.getElementById('btn-monod').classList.remove('active');
            document.querySelector('.slider-container:nth-child(4)').style.display = 'block';
            updatePlot();
        });

        document.getElementById('btn-bifurcation').addEventListener('click', () => {
            currentView = 'bifurcation';
            document.getElementById('btn-bifurcation').classList.add('active');
            document.getElementById('btn-nontrivial').classList.remove('active');
            updatePlot();
        });

        document.getElementById('btn-nontrivial').addEventListener('click', () => {
            currentView = 'nontrivial';
            document.getElementById('btn-nontrivial').classList.add('active');
            document.getElementById('btn-bifurcation').classList.remove('active');
            updatePlot();
        });

        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', () => {
                let value = slider.value;
                let id = slider.id;
                let display = document.getElementById(`value-${id}`);
                display.innerText = value;
                updatePlot();
            });
        });

        // Initial plot rendering
        updatePlot();
    </script>
</body>
</html>
